css:
@-moz-document url-prefix("http://eloquentjavascript.net/") {
h1, h2, h3, h4, h5, h6 {
    font-family: 'Bitstream Charter', 'DejaVu Serif', Georgia, serif !important;
}

body { background-color: #F5F5F5; }

div.content {
    font-family: 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif !important;
    font-size: .9rem !important;
	background-color: #F5F5F5 !important;
    color: #151515 !important;
}

div.console {
  background-color: #B5C9C6 !important;
}

div.console .header {
  text-align: right !important;
  letter-spacing: .8em !important;
  font-size: 50% !important;
  color: #7c9490 !important;
  font-weight: bold !important;
  font-family: times, serif !important;
  padding-top: 1px !important;
  padding-right: .5em !important;
  background-color: #B5C9C6 !important;
}

div.outputhead {
  font-size: 70% !important;
  line-height: 90% !important;
  padding: 2px .4em !important;
  color: #7c9490 !important;
  border-bottom: 1px solid #CCDDDD !important;
  background-color: #EEF4F4 !important;
  font-family: times, serif !important;
}

div.outputinner, .CodeMirror-lines pre, div.editor input {
  border: 0 !important;
  padding: 2px !important;
  font-family: 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace !important;
  font-size: .8rem !important;
}

div#controls {
  background-color: #B5C9C6 !important;
}

div.content pre {
    font-family: 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace !important;
    font-size: .9rem !important;
    background-color: #202020 !important;
	padding: 10px !important;
	color: #F5F5F5 !important;
	border-radius: 10px !important;
}

tt, code {
	font-family: 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace !important;
	font-size: .9rem !important;
	background-color: #E0E0E0 !important;
	padding-left: .25em !important;
	padding-right: .25em !important;
}

.variable, .atom, .keyword, .string, .preformatted, .property, .comment {
    font-family: 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace !important;
}

.variable       { color: #F4BF75 !important; }
.property       { color: #D28445 !important; }
.preformatted   { color: #D0D0D0 !important; }
.variabledef    { color: #6A9FB5 !important; }
.localvariable  { color: #6A9FB5 !important; }
.atom           { color: #90A959 !important; }
.keyword        { color: #AA759F !important; }
.string         { color: #AC4142 !important; }
.class          { color: #8F5536 !important; }
.comment        { color: #505050 !important; font-weight: bold !important; }

hr { background-color: #FFF !important; border: 1px solid #B0B0B0 !important; }

}
