css:
@-moz-document domain("teamtreehouse.com") {
body {
  background-color: #444;
  font-family: 'Open Sans' !important;
}

.contained, .quiz-answers-container, .quiz-answers-breakdown li {
  background-color: #333;
}

.contained .secondary-heading h1, .contained .secondary-heading h2, h2, .contained .secondary-heading h3, h3 a, .module-instructor h4 {
  color:steelblue;
}

a.button.button-full {
  opacity:0.5;
}

.steps-nav li a, .disclaimer, .quiz-answers-breakdown li {
background: none;
}

div #question h1 p {
color: steelblue;
}

.challenge-heading h1 p {
color: #ABAFB3;
}

.feedback.neutral {
background: #4D4D4D;
}  
  
.quiz-answers-container li a:hover, .quiz-answers-container li a.selected.inactive, .quiz-answers-container li a:hover.inactive.selected {
background: #8A8A8A;
color: #000;
}

.achievement-steps {
background: rgba(74, 77, 82, 1);
}

.total-points h1 {
color: #6FA9DD;
}
}

@-moz-document domain("teamtreehouse.com") {
.grid-75 {
float: none;
width: 100%;
}

.grid-25 {
float: none;
width: 100%;
}

/* video player size */
element.style {
width: 100%;
height: 700px;
}
.video-container {
padding: 0;
}
  
/* transcript */
div#featurette-4.grid-50 {
display: none;
}
}
