css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
div.l-header .logo { float: right!important; }

div.l-header #tabs_outer_container { float: left!important; position: relative; }
div.l-header .tab_bar { float: left!important; }
.tab_bar .tab { float: right; }
div.l-header .tab.iconic.ui_search, .l-header-container--refresh .l-header .ui_search { float: left; }
div.l-header .tab_bar .tab .tab_notice { left: auto; right: 37px; }
.compose-button { float: left; }

#right_column { float: left!important; }
#left_column { float: right!important; }

#posts.posts { margin: 0 85px 0 0!important; }

.post_avatar { left: 561px; }
.post_avatar:after { 
		left: 0px; 
		border-right: 0px; 
		border-left: 9px solid #fff;
        margin-left: -21px;
}

.post_full .post_info { float: right!important; text-align: right; padding-right: 0px; }
.post_source { float: left!important; }
.post .post_info .reblog_follow_button i { width: 12px; z-index: 0; }
.post .post_info a.reblog_follow_button { width: 12px; }

.post_full .post_notes { float: right; max-width: 225px; }
.post_full .post_controls { float: left; max-width: 250px; }
.post_full .post_notes_label { float: right; }
.post_full .post_control { float: left; margin: 10px 12px 10px 0; }
.post_full .post_control.reblog { margin-right: 10px; }
.post_full .post_control:first-child { margin: 10px 12px 10px 0; }

.elevator-wrapper { left: 15px; right: initial; }
#return_to_bottom { left: 43px; right: initial; }
}
