css:
/* GENERAL */
#sidebar,
#bookmarksPanel,
#history-panel {
height: 100% !important;
background: none !important;
}

#sidebar-box {
width: 326px !important; /**/
-moz-appearance: none !important;
position: fixed !important;
top: 90px !important; /**/
bottom: 20px !important; /**/
padding: 14px !important;
border-image-source: url("data:image/png;base64,") !important;
border-image-slice: 15 fill !important;
border-image-width: 15px !important;
border-image-repeat: repeat !important;
-moz-window-shadow: none !important;
background: none !important;
color: #fff !important;
opacity: 0.95 !important;
}
/* HEADER */
#sidebar-header * {
color: #fff !important;
}
/* SIDEBAR LIST */
#sidebar-box > browser {
margin: 0px 0px 0px -1px !important;
padding: 0px !important;
/*padding-bottom: 25px !important;*/
width: 615px !important; /**/
height: 684px !important; /**/
}
/* SCROLLBARS */
#bookmarksPanel scrollbar,
#history-panel scrollbar {
display: none !important;
}

/* SIDEBAR TREE */
#bookmarksPanel treechildren,
#history-panel treechildren {
color: #eee !important;
}

/* SELECTED ROW */
#bookmarksPanel treechildren::-moz-tree-row(selected),
#history-panel treechildren::-moz-tree-row(selected) {
background: -moz-linear-gradient(top, #6f81f5, #3f51f2) repeat-x !important;
border: none !important;
color: #fff !important;
}
/* SELECTED ITEM (GRAPHITE) */
#bookmarksPanel:-moz-system-metric(mac-graphite-theme) treechildren::-moz-tree-row(selected),
#history-panel:-moz-system-metric(mac-graphite-theme) treechildren::-moz-tree-row(selected) {
background: -moz-linear-gradient(top, #7f868d, #69717c) repeat-x !important;
border: none !important;
color: #fff !important;
}
/* DISCLOSURE TRIANGLES */
#bookmarksPanel treechildren::-moz-tree-twisty,
#history-panel treechildren::-moz-tree-twisty {
-moz-appearance: none !important;
list-style-image: url("data:image/png;base64,")
!important;
-moz-image-region: rect(0px 7px 9px 0px) !important;
width: 7px !important;
margin-right: 2px !important;
}
#bookmarksPanel treechildren::-moz-tree-twisty(open),
#history-panel treechildren::-moz-tree-twisty(open) {
-moz-appearance: none !important;
-moz-image-region: rect(0px 25px 9px 16px) !important;
width: 9px !important;
margin-right: 0px !important;
}

#sidebar-splitter{display:none}
#sidebar-header{
-moz-appearance: none !important;
width: 100% !important;
background: transparent !important;
}
