css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://newanimehd.blogspot.com/") 
{



/*text&background*/



html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer



{

color: white !important;

background: url('http://i.imgur.com/Y6O1tRJ.jpg') left top!important;

}



select, button



{

background: url('http://i.imgur.com/Y6O1tRJ.jpg') left top!important;



}



div, span, a



{

background: url('http://i.imgur.com/Y6O1tRJ.jpg') left top !important;



}



legend, select, input, span, code, textarea, pre, label, div, dd



{

color: white !important;

}





/*links*/



a



{

color: #b3b6bd !important;

}



a:visited



{

color: #d8dbdf !important;

}



/*highlight*/



textarea, pre, input, code



{

background: url('http://i.imgur.com/Y6O1tRJ.jpg') left top !important;



}



}
