css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.facebook.com") {
  
  
/* Background */  
body{
  background-image: url('http://static.squarespace.com/static/4f52459424ac0d7fb252fb9d/t/51678665e4b0f45e71bdfe01/1365739109879/Chevron%20Background%20-%20TEAL.png') !important;
  background-attachment: fixed !important;
  background-size: cover !important;
  background-position: center center !important;
}
  
#contentCol{
  background: transparent !important;
  border-left: none !important;
}
  
  
  
/* Nav Bar */  
#blueBarNAXAnchor {
    background: none;
    background-color: rgba(0,0,0,1);
}
  
._4f7n:after, ._4f7n{
  background: none;
  border: none;
}


  
/* Left Column */
._bui ._5afe, ._bui .subitem {
    color: #fff;
}
  
#leftCol{
  margin-top: 12px;
  background-color: rgba(0,0,0,0.7);
  border-radius: 4px;
  padding-right: 4px;
  width: 161px !important;
}  

#pagelet_welcome_box a {
  color: #c4ccda !important;
}
  
.navHeader, .navHeader a {
  font-size: 10px !important;
  color: #7a8292 !important;
}  
  
._2jq5 {
  border: none;
}
  
.buttonWrap{
  margin-right: 5px;
}
  

  
/* Center Column */
._4-u2 {
  border: none;
  border-radius: 4px;
}
  
  
  
/* Right Column */
#pagelet_rhc_footer{
  background: #fff;
  margin-top: 10px;
  border-radius: 4px;
}
  
  
  
/* Chat Sidebar */
.fbChatSidebar{
  background-color: rgba(0,0,0,0.7) !important;
  border: none;
  box-shadow: none !important;
}
  
._42fz a, .fbFeedTicker .tickerStoryClickable a {
  color: #bbb !important;
  font-weight: bold;
}
  
._5pr2.fbChatSidebar .fbFeedTickerStory .fbFeedTickerBorder{
  border-top: none;
}
  
._5pr2 .fbSidebarGripper {
  border-top: none;
  background-color: #fff;
  height: 2px;
}
  
.fbChatSidebarBody {
  padding-top: 5px;
}
  


/* Chat Box */    
div._50mz .fbDockChatTabFlyout{
    height: 585px !important;
}
    
div.fbNubFlyoutOuter{
    max-height: 590px !important;
}
  
.fbNubFlyout {
    box-shadow: none;
}
  
._50mz .fbNubFlyoutInner, ._5q5b .fbNubFlyoutTitlebar, ._5q5b ._50-v .noTitlebar, ._5q5b ._50-v.openToggler .fbNubFlyoutTitlebar {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
  
._50mz .fbNubFlyoutInner {
  background: none;
}
  
.clearfix.fbNubFlyoutTitlebar.titlebar, ._5q5b .fbNubFlyoutTitlebar {
    background: #303030;
    border: none;
    position: relative;
}
  
.titlebarTextWrapper {
  margin: 0px 8px 5px !important;
  padding-top: 5px;
}

.fbDockChatTabFlyout .fbNubFlyoutBody {
  background: #e0e0e0;
  border: none;
}
  
.fbNubFlyoutBody, .fbNubFlyoutFooter {
  border: none !important;
}
  

  
/* Timeline */
._4lh .fbTimelineTimePeriod {
  background-color: transparent;
}
  
._6-7 {
  background-color: #ccc;
  border-radius: 10px;
}
._6-6, ._6_7, ._6-7{
  border: none;
}
  
._513x {
  background-image: none;
}
  
._4-u2 {
  border-radius: 4px;
  border: none;
}  
  
.topBorder, .bottomBorder {
  display: none;
}
  
.timelineReportContainer {
  border-radius: 4px !important;
}
  
._70l, ._6dh ._5142 {
  border: none;
  border-radius: 4px 4px 0 0;
}
  
._6dh ._1dsl {
    border: none;
}
  
.fbTimelineCapsule .timelineUnitContainer {
  border-radius: 4px;
}
  
._55d0 {
  background: none;
}
 

}
