css:
@-moz-document url-prefix("http://mixlr."), url-prefix("https://mixlr.") {
/* Theme Created by HeartFire @ http://heartfire-art.deviantart.com/ 
Feel free to contact me for support should you need it, either on my email: HeartFire@live.se or URL above*/
}

@-moz-document url-prefix("http://mixlr."), url-prefix("https://mixlr.") {
body {
background-image:url('http://fc02.deviantart.net/fs70/f/2014/121/7/7/wub_wub_wub_by_yakovlev_vad_d7gou8p_by_heartfire_art-d7gpehx.png') !important; 
background-size: cover !important;
background-repeat:no-repeat !important; 
background-attachment:fixed !important; 
background-position:center !important; 
text-shadow: 0px 0px 8px #000 !important; 
}

#main, #footer, #sm2-container, .big_wrapper, #showreel, #main_content, html {
background-color: transparent !important;
}

h3 {
color: white !important
}

.player_header {
background-color: rgba(30, 30, 30, 0.7) !important;
}

.mixlr_player {
background-color: rgba(60, 60, 60, 0.9) !important;
box-shadow: 0px 0px 10px 2px rgba(0, 0, 0, 0.7) !important;
transition: all 0.25s linear !important;
-webkit-transition: all 0.25s linear !important;
-moz-transition: all 0.25s linear !important;
-o-transition: all 0.25s linear !important
}

.mixlr_player:hover {
background-color: rgba(70, 70, 70, 0.95) !important;
transition: all 0.25s linear !important;
-webkit-transition: all 0.25s linear !important;
-moz-transition: all 0.25s linear !important;
-o-transition: all 0.25s linear !important
}

.player_bar {
background-color: rgba(100, 100, 100, 0.75) !important;
}

.category_list {
background-color: #606060 !important;
}

.category_select {
background-color: #808080 !important;
}

#header>.category_select>.select>.category_holder>.category_menu>li>ul>li {
background-color: #424242; 
}

#fancybox-outer-title, #email_signup {
background-color: #141414 !important;
color: #999999 !important;
}

#fancybox-content {
background-color: #141414 !important;
border: 10px solid #141414 !important;
}

time, .player_category, .date, .login, .alternate {
color: #999999 !important;
}

button {
background-color: #1E1E1E !important;
border-bottom: 2px solid #666666 !important;
}

a:hover {
color: white;
}

#user_username, #user_email, #user_password {
background-color: #3C3C3C !important;
color: #999999 !important;
border: none !important;
}

form > button {
background: transparent !important;
border-bottom: none !important;
}

#main_search_results {
background: rgba(73, 73, 73, 0.75) !important;
}

#main_search_results ul li.active {
background: rgba(125, 125, 125, 0.75) !important;
}

#main_search_results ul li .broadcast_category {
background: rgba(200, 200, 200, 0.6) !important;
padding: 0 0 11px 10px !important;
color: #FFFFFF;
height: 30px !important;
}

#main_search_results ul li .search_broadcast_stats {
background: rgba(200, 200, 200, 0.6) !important;
height: 30px !important;
}

#main_search_results ul li .broadcast_category:before, #main_search_results ul li .search_broadcast_stats:before {
content: '';
display: block;
position: absolute;
z-index: 2;
left: -25px;
top: 0;
height: 30px;
width: 30px;
background: -moz-linear-gradient(left,rgba(150,150,1500) 0,rgba(150,150,1500.01) 1%,rgb(150, 150, 150) 79%,rgba(150, 150, 150, 0.6) 100%) !important;
background: -webkit-gradient(linear,left top,right top,color-stop(0%,rgba(150,150,150,0)),color-stop(1%,rgba(150,150,1500.01)),color-stop(79%,rgb(150, 150, 150)),color-stop(100%,rgba(150, 150, 150, 0.6))) !important;
background: -webkit-linear-gradient(left,rgba(150,150,1500) 0,rgba(150,150,1500.01) 1%,rgb(150, 150, 150) 79%,rgba(150, 150, 150, 0.6) 100%) !important;
background: -o-linear-gradient(left,rgba(150,150,1500) 0,rgba(150,150,1500.01) 1%,rgb(150, 150, 150) 79%,rgba(150, 150, 150, 0.6) 100%) !important;
background: -ms-linear-gradient(left,rgba(150,150,150,0) 0,rgba(150,150,1500.01) 1%,rgb(150, 150, 150) 79%,rgba(150, 150, 150, 0.6) 100%) !important;
background: linear-gradient(to right,rgba(150,150,150,0) 0,rgba(150,150,150,0.01) 1%,rgb(150, 150, 150) 79%,rgba(150, 150, 150, 0.6) 100%) !important;
}

#main_search_results ul li .search_broadcast_stats li {
color: #FFFFFF !important;
}

#main_search_results ul li .search_broadcast_stats li i:before {
color: #FFF !important
}

header#main_header {
transition: all 0.25s linear !important;
-webkit-transition: all 0.25s linear !important;
-moz-transition: all 0.25s linear !important;
-o-transition: all 0.25s linear !important;
background: rgba(0, 0, 0, 0.7) !important;
box-shadow: 0px 2px 10px 2px rgba(0, 0, 0, 0.7) !important
}

header#main_header:hover {
transition: all 0.25s linear !important;
-webkit-transition: all 0.25s linear !important;
-moz-transition: all 0.25s linear !important;
-o-transition: all 0.25s linear !important;
background: rgba(0, 0, 0, 0.9) !important;
}

#main_search form.search_site.active input[type="text"] {
background: rgba(255, 255, 255, 0.9) !important;
}

#main_search form.search_site input[type="text"] {
background: rgba(0, 0, 0, 0.75) !important;
}

#broadcast_view {
background-image: none !important;
background-color: transparent !important;
}

#broadcast_profile {
background: transparent !important;
}

.description, #about>.meta {
background-color: rgba(250, 250, 250, 0.75);
border-radius: 5px;
padding: 5px;
box-shadow: 0px 0px 10px 2px rgba(0, 0, 0, 0.7) !important;
}

.description {
width: 250px !important
}

#broadcaster_details {
background-color: rgba(250, 250, 250, 0.75);
box-shadow: 10px 5px 10px 2px rgba(0, 0, 0, 0.7) !important;
}

#broadcaster_details>#user_details>time>a.share {
background-color: transparent !important;
}

#broadcaster_details .user_links>li>a.active {
transition: background-color 0.25s linear !important;
-webkit-transition: background-color 0.25s linear !important;
-moz-transition: background-color 0.25s linear !important;
-o-transition: background-color 0.25s linear !important;
background-color: rgba(40, 40, 40, 0.85)
}

#broadcaster_details .user_links>li>a:hover {
transition: background-color 0.25s linear !important;
-webkit-transition: background-color 0.25s linear !important;
-moz-transition: background-color 0.25s linear !important;
-o-transition: background-color 0.25s linear !important;
background-color: rgba(50, 50, 50, 0.75);
}

#broadcaster_details .user_links>li>a {
transition: background-color 0.25s linear !important;
-webkit-transition: background-color 0.25s linear !important;
-moz-transition: background-color 0.25s linear !important;
-o-transition: background-color 0.25s linear !important;
}

#broadcaster_details .user_links>li.profile>a.active:hover {
transition: background-color 0.25s linear !important;
-webkit-transition: background-color 0.25s linear !important;
-moz-transition: background-color 0.25s linear !important;
-o-transition: background-color 0.25s linear !important;
background-color: rgba(24, 24, 24, 0.9);
}

#chat_box {
background-color: rgba(255, 255, 255, 0.7);
border: 1px solid rgba(242, 242, 242, 0.8);
}

#broadcaster_details>#broadcaster_info>b, time {
color: #666 !important;
}

.container {
background: rgba(255, 255, 255, 0.75) !important;
border-bottom-right-radius: 5px;
border-bottom-left-radius: 5px;
box-shadow: 0px 5px 10px 2px rgba(0, 0, 0, 0.7) !important;
}

#showreel.container {
background: transparent !important;
border-radius: 0px !important;
box-shadow: none !important;
}

#settings .hints_copy, #embed_artworkform, .player_embed_code {
border-radius: 5px;
background-color: rgba(242, 242, 242, 0.75) !important;
}

.player_embed_code, li.plan {
border-radius: 3px;
background-color: rgba(242, 242, 242, 0.75) !important;
height: 500px;
margin-right: -2px !important
}

.price_plans .plans>ul>li .details p {
background: transparent;
}

.note {
border-radius: 5px;
background-color: rgba(242, 242, 242, 0.75) !important;
}

#info_header {
background-color: rgba(255, 255, 255, 0.5) !important;
}

.my_mixl_container, #my_mixlr, #my_mixlr .header, .my_mixlr, .broadcast_list, .headings, ul.broadsast_list>li {
background: transparent !important
}

#my_mixlr .my_mixlr_container .broadcast_list li a {
background: rgba(0, 0, 0, 0.75) !important;
}
}
