css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
body 	
{
background-image:url('http://i.imgur.com/OJ9tGQg.jpg');
background-repeat:no-repeat;
background-position:center fixed;
}
.userbar{
	border:1px solid #b6b6b6;
	padding:5px;
	background:#EBEBEB;
	margin-top:-5px
}
.hangman{
  background: url(http://fc09.deviantart.net/fs71/f/2012/275/7/7/black_and_grey_stripe_pattern_by_wiizkiid-d5gm6uv.png) center;
  color: #eff;
  }
.header {
	position: relative;
	height: 56px;
	background: url(http://images2.layoutsparks.com/1/142209/black-white-stripes-pattern.jpg);
}
.ps-room.ps-room-light {
	background: url(http://www.propgallery.com/wp-content/gallery/background_1/bg-white.jpg);
	color: black;
}
.tabbar {
	position: absolute;
	bottom: 1px;
	left: 0;
	right: 0;

	display: block;
	list-style: none;
	margin: 0;
	padding: 0 0 0 0;
	height: 37px;
	text-align: left;

	color: white;
	text-shadow: 0 1px 0 black;
	font-size: 10pt;

	line-height: 100%;
}
.maintabbarbottom {
	content: "";
	display: block;
	position: absolute;
	left: 0;
	right: 0;
	bottom: -6px;
	height: 6px;
	background: linear-gradient(to bottom, rgba(0,0,0,.6) 0%, rgba(0,0,0,0) 100%);
	border: none;
	border-left: 0;
	margin: -1px 0 0 0;
	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);
	-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);
  	z-index:2;
	box-shadow: none;
}
.tabbar a.button:hover{
    background: #100;
    color: #ecf0f1
    }
.tabbar a.button span {
	display: block;
	overflow: hidden;
}
.tabbar.maintabbar {
margin-left: 170px;
margin-right: 0px;
overflow: hidden;
  height: 55px;
  background-position: right;
  background-repeat: no-repeat;
}

.tabbar a.button.cur, .tabbar a.button.cur:hover, .tabbar a.button{
color: #ecf0f1;
background: #333333;
box-shadow: none;
  border: none;
cursor: default;
top: 0px;
  border-radius: 5px;
text-shadow: none;
  margin-left: 5px;
  margin-top:15px;
}
.closebutton,
.minimizebutton {
	text-decoration: none;
	font-size: 14px;
	color: #FFFFFF;
	text-shadow: none;
	border: 0;
	padding: 0;
	margin: 0;
  	top: 19px;
	background: transparent;
	cursor: pointer;
}
.tabbar .closebutton, .tabbar a.cur+.closebutton {
top: 19px;
}
.closebutton:hover {
color: #a00;
}
.closebutton {
color: #ecf0f1;
}

.userlist{
	background: url(http://fc06.deviantart.net/fs71/i/2010/362/4/3/custom_box_bg_3_by_idislikethisbit-d35urz7.jpg);
  	color: white;
}

.userlist li button:hover {
background: black;
}

.userlist li{
border-bottom: 1px solid #1b1b1b;
}
.pm-log {
	min-height: 100px;
	max-height: 300px;
	background: url(http://www.echodeck.net/webflyer/images/cool_present_bg.gif);
	color: #FFFFFF;
	word-wrap: break-word;
	border: 1px solid #AAAAAA;
	border-top: 0;
	border-bottom: 0;

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.pm-window h3 {
    background: url(http://images2.layoutsparks.com/1/237102/miaka-dbl-bg-sky.gif);
  	border: 2px;
}
.pm-window.focused h3, .pm-window.focused h3:hover {
background: url(http://freedesignfile.com/upload/2012/09/sun-background-3.jpg);
border: 2px;
}
.pm-log-add{
        background: #a00;
  		border: 2px;
    	border-color: #000;
}
.chat-log {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 40px;
	font-size: 10pt;
	background: url(http://i.imgur.com/fTXBQ98.jpg)	no-repeat scroll right top;
	color: black;
	word-wrap: break-word;

	overflow: auto;
	overflow-x: hidden;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.battle-log{
  background: url(http://i.imgur.com/fTXBQ98.jpg);
}
.chat-log-add,
.battle-log-add{
background: url(http://www.leviu.ca/images/bg.jpg);
}
/*"Look for a battle" buttons (when hovered over)*/
.menugroup .button:hover {
   	background: #100;
  	color: #eff
}

/*"Look for a battle" buttons*/
.menugroup .button {
    	background: #eff;
  		color: #100
}
.ladder table, .ladder td, .ladder th {
  	background: #dfdfdf;
	border-collapse: collapse;
	border: 2px solid #eff;
  	font-weight: bold;
}
.ladder td, .ladder th {
	padding: 3px 5px;
  	border: 2px solid #100;
    font-weight: bold;
}
.ladder th {
	text-align: left;
	font-size: 9pt;
  	font-weight: bold;
	background: #EEEEEE;
}
.ps-room .battle{
	top: 60px;
	border-width: 0px;
}
  
.foehint{    
position: relative;
top: 45px;
}        
        
.battle {
    border: 0px solid #AAAAAA;
    width: 640px;
    height: 360px;
    overflow: visible;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
}
    
.backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
}

.backdrop:after{
	display: block;
	content: "";
	background: url(http://i.imgur.com/zQ6bUvr.png);
	position: absolute;
	top: 0;
	left: 0;
	width: 640px;
    height: 360px;
}

.ps-room .battle-controls {
top: 430px;
}

.turn {
    position: absolute;
    top: -42px;
    left: 270px !important;
    width: 101px;
    height: 17px;
    font-size: 8pt;
    font-weight: bold;
    margin: 0px;
    padding: 0px;
    padding-top:35px;
    border: 0;
    border-radius: 0;
    opacity: 1 !important;
    background: url(http://i.imgur.com/Ng0wOlV.png);
    text-align: center;
    color: #FFF;
}


  
  


.rightbar {
position: absolute;
top: -60px;
right: 0;
height: 100px;
width: 320px;
background-color: rgba(255,255,255,0);
border: 0px solid rgba(215,215,215,0.5);
color: #777777;
background-image: url(http://i.imgur.com/WEdUzyR.png);
background-position: 0px -35px;
}

.rightbar .trainer {
margin-top: 0px;
text-align: right;
}

.rightbar .trainersprite{
position: absolute;
top: 5px;
left: 40px;
opacity: 1 !important;
}

.trainer strong {
display: block;
font-size: 8pt;
overflow: hidden;
padding-top: 45px;
padding-right: 10px;
color: #111;
}
  
  
.rightbar .trainer .teamicons {
float:left;
position: relative;
top: -42px;
left: 108px;
}










.leftbar {
position: absolute;
top: -60px;
left: 0;
height: 100px;
width: 320px;
background-color: rgba(255,255,255,0);
border: 0px solid rgba(215,215,215,0.5);
color: #777777;
background-image: url(http://i.imgur.com/Y68Rmc5.png);
background-position: 0px -35px;
}

.leftbar .trainer {
margin-top: 0px;
text-align: left;
}

.leftbar .trainersprite{
position: absolute;
top: 5px;
right: 40px;
opacity: 1 !important;
}

.leftbar .trainer strong {
display: block;
font-size: 8pt;
overflow: hidden;
padding-top: 45px;
padding-left: 10px;
}
  
  
.leftbar .trainer .teamicons {
float:right;
position: relative;
top: -42px;
right: 108px;
}



.weather {
	text-shadow:none;
    color: rgba(0,0,0,0);
}

.weather em small {
	font-size: 7pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left:0;
    top: 85px;
    width: 95px;
    text-align:center;
  }
  
.sunweather, .sunnydayweather {
background: url(http://i.imgur.com/zNp4BiP.jpg) no-repeat scroll left top;
}

.rainweather, .raindanceweather {
background: url(http://i.imgur.com/xRbPLwC.jpg) no-repeat scroll left top;
  }
  
.hailweather {
background: url(http://i.imgur.com/NxZzwHi.gif) no-repeat scroll left top;
  }

.sandstormweather {
background: url(http://i.imgur.com/PhAU4CK.jpg) no-repeat scroll left top;
}

.pseudoweather{
background: url(http://i.imgur.com/diinudq.png) no-repeat scroll left top;
}
    


.switchmenu button, .switchmenu button:disabled{
	background: url(http://i.imgur.com/qYAMtSx.png) !important;
    border: none;
    box-shadow: none;
    border-radius: 0;
  	font-size: 8pt;
}

.switchmenu button{
	color: white;            
    text-shadow: 1px 1px black;
}

.switchmenu button:disabled{
    opacity: .5;
    }

.movemenu button{
	width: 150px;      
	height: 50px;
  	border: 5px;
    box-shadow: none;
    color: white;
    text-shadow: 1px 1px black;
    font-size: 10pt;
    padding-top: 10px;
}

.movemenu button small{
text-align: center;
width: 62px;
margin-top:2px;
