.infobox, .ladder{
	position: relative;
	border: 1px solid #181818;
  	border-radius: 2px;
	box-shadow: inset 0px 0px 0px 1px rgba(0,0,0,.2);
    margin-top: 5px;
    color: #1b1b1b;
    background-color: #ecf0f1;
}
.ps-popup{
  background: #ecf0f1;
  border: 2px solid;
  color: #100;
  border-top-left-radius: 20px solid;
  }  
.utilichart:after, .infobox:after, .ladder:after, #tooltipwrapper .tooltip:after{
	display: block;
	content: "";
    background: url(http://i.imgur.com/GCuRjnR.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
}
}
