css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("my.msoe.edu") {

html{
    margin: 0 auto !important;
    width: 100% !important;
    background: #363636;
    overflow-x: hidden !important;
}

body{
    background: white !important;
}

/* START OF TEXT STYLING */
h1{
    color: #990000 !important;
    font-size: 15px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
}

h2{
    color: #990000 !important;
    font-size: 15px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    background: none !important;
    color: #990000 !important;
}

h2 a{
    background: none !important;
    color: #990000 !important;
}

h3{
    color: #990000 !important;
    font-size: 15px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    background: none !important;
}

h4{
    background: #eee !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 200 !important;
}

a {
    color: #990000 !important;
}

p {
    color: #252525 !important;
}
/* END OF TEXT STYLING */


/* START OF ALERT STYLES */
.feedbackMessage {
    background: #e5eef7 !important;
    border: 1px solid #96c2ee !important;
    border-radius: 3px !important;
}
/* END OF ALERT STYLES */


/* START OF LAYOUT CONTROLS */
#MAINFORM{
    margin: 0 auto !important;
    width: 100% !important;
    background: white; /* Old browsers */
}

#masthead{
    margin: 0 !important;
    height: 0px;
    width: 100% !important;
    display: none;
}

#welcomeBackBar{
    margin-bottom: 40px !important;
    height: 35px;
    width: 100%;
    background: white !important;
    box-shadow: 0px 1px 3px rgba(0,0,0,0.2) !important;
}

#userWelcome{
    float: right;
    padding-right: 70px;
    margin-top: 7px;
    background: none;
    color: #252525 !important;
    font-size: 12px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    font-style: none !important;
}

#userWelcome strong{
    text-decoration: bold !important;
    font-weight: 600 !important;
}

#errorMessage {
    width: 90% !important;
    margin: 0 auto !important;
}

#accountInfo,#logout{
    color: #252525 !important;
    text-transform: lowercase;
}

#userLogin{
    margin: 0px 0px 0px 0px;
    float: right;
    padding-right: 70px;
    color: #252525 !important;
    font-size: 12px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
}

#userLogin label{
    margin: 0px 5px 0px 5px !important;
}

#userName{
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    padding: 2px 2px 2px 7px;
    height: 33px;
    background-color: white !important;
    border: 2px solid #eee !important;
    border-bottom: 2px solid #990000 !important;
    color: #252525 !important;
    font-size: 13px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    transition: border 0.5s;
}

#password{
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    padding: 2px 2px 2px 7px;
    height: 33px;
    background-color: white !important;
    border: 2px solid #eee !important;
    border-bottom: 2px solid #990000 !important;
    color: #252525 !important;
    font-size: 13px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    transition: border 0.5s;
}

#password:focus, #userName:focus{
    border: 2px solid #990000 !important;
    outline: none !important;
}

#btnLogin{
    margin: 0px 0px 0px -6px !important;
    height: 33px;
    border-bottom: 3px solid #990000 !important;
    box-sizing: border-box;
    padding: 2px 5px 2px 5px;
    background-color: #990000 !important;
    font-family: 'Arial', sans-serif !important;
    font-weight: bold !important;
    font-size: 14px !important;
    text-transform: uppercase !important;
    color: white !important;
    transition: background-color 2s;
}

#btnLogin a {
    height: auto !important;
    width: auto !important;
}

/*Creating Tabs*/
#headerTabs{
    margin: 0px auto 0px auto !important;
    padding-top: 0px;
    width: 90% !important;
    height: 45px !important;
}

#headerTabs ul{
    padding: none !important;
    margin: 0px 0px 0px 0px !important;
    height: 45px !important;
}

#headerTabs li{
    margin: 0px 5px 0px 0px !important;
    height: 45px !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    padding: 7px 7px 7px 7px !important;
    background: #252525 !important;
    border: 5px solid #252525 !important;
    border-bottom: none !important;
    text-transform: uppercase !important;
    display: inline-block !important;
}

#headerTabs li.selected{
    background: #fff !important;
    z-index: 10 !important;
    position: relative !important;
    margin-top: -1px !important;
}

#headerTabs a{
    display: inline-block !important; 
    width: 100% !important;
    height: 100% !important;
    background: none !important;
    color: #fff !important;
    font-family: 'Arial-Narrow', sans-serif !important;
    font-style: bold !important;
    font-size: 15px !important;
    margin: 3px 3px 0px 3px !important;
}

#headerTabs a:hover{
    text-decoration: none !important;
}

#headerTabs li.selected a{
    color: #252525 !important;
}

#mainCrumbs{
    margin: -4px auto 0px auto !important;
    position: relative !important;
    z-index: 0 !important;
    height: 80px;
    width: 100% !important;
    border-bottom: none !important;
    border-top: 5px solid #252525 !important;
    background: rgb(237,237,237) !important; /* Old browsers */
    background-image: url(https://raw.github.com/wesleyzloza/myMSOE-Skin/master/crumbs_bg.png) !important;
    font-size: 0px !important;
}

#mainLayout{
    margin: 20px auto 0px auto !important;
    position: relative;
    z-index: 1;
    box-sizing: border-box;
    -mox-box-sizing: border-box;
    padding-bottom: 0px !important;
    background: white !important;
    width: 90% !important;
    border-radius: 5px !important;
    border: 1px solid #ccc;
}

#PORTLETGRID{
    margin-top: -56px !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    padding-left: 20px !important;
}

/* Removing Styles */
.sideSection,#quickLinks,#myPages,#contextPages{
    background: none !important;
    border: none !important;
}

/* EDITING SIDE MENU'S */
#sideBar{
    background: #f9f9f9 !important;
    border-radius: 5px !important;
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    border-right: 1px solid #ccc !important;
}

/* START OF "This Context" LINKS STYLES */
#thisContext, .sideSection {
    margin: 0px !important;
    width: 100% !important;
    display: block !important;
    background: none !important;
}

#thisContext h2 {
    margin: 0px 0px -2px -5px !important;
    display: block !important;
    width: 100% !important;
}

#thisContext h2 a{
    display: block !important;
    padding: 8px 0px 0px 10px !important;
    border-bottom: 1px solid #ddd !important;
    text-transform: uppercase !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: bold !important;
    color: #333 !important;
}

#thisContext h2 a:hover{
    text-decoration: none !important;
}

#thisContext #contextPages li {
    margin: 0px 0px 0px 0px !important;
    min-height: 30px !important;
    background: #f7f7f7 !important;
    border-top: 1px solid #fff !important;
    border-bottom: 1px solid #ccc !important;
    padding: 0px !important;
}

#thisContext #contextPages li a {
    height: auto !important;
    display: block;
    margin: 0px !important;
    box-sizing: border-box; 
    -moz-box-sizing: border-box;
    padding: 5px !important;
    padding-top: 6px !important;
    padding-left: 10px !important;
    color: #252525 !important;
    font-size: 13px !important;
    background: none !important;
    text-decoration: none !important;
}

#thisContext #contextPages li a:hover {
    background: #eee !important;
}


#thisContext #contextPages .currentPage{
    background: #white !important;
    padding: 0px !important;
}

#thisContext #contextPages .currentPage a{
    font-weight: bold !important;
}

#thisContext #contextPages .currentPage #pagePortlets{
    display: block !important;
    width: 100% !important;
    margin: 0px !important;
    background: white !important;
    border-top: 1px solid #ccc !important;
    border-bottom: 1px solid #eee !important;
}

#thisContext #contextPages .currentPage #pagePortlets li{
    display: block !important;
    padding: 0px 0px 0px 0px !important;
    width: 100% !important;
    list-style: none !important;
    background: white !important;
    border-top: 1px solid #e9e9e9 !important;
    border-bottom: none !important;
}

#thisContext #contextPages .currentPage #pagePortlets li a{
    width: 100% !important;
    margin: 0px !important;
    box-sizing: border-box !important; 
    -moz-boxi-sizing: border-box !important;
    padding-top: 10px !important;
    padding-bottom: 10px !important;
    padding-left: 10px !important;
    font-weight: 300 !important;
    text-decoration: none !important;
}

#thisContext #contextPages .currentPage #pagePortlets li a:hover{
    background: #f9f9f9 !important;
}

/* END OF "This Context" LINKS STYLES */



/* START OF QUICK LINKS STYLES */
#myPages {
    display: none !important;
}

#quickLinks {
    margin: 0px !important;
}

#quickLinks h3{
    display: block !important;
    padding: 20px 0px 0px 10px !important;
    border-bottom: 1px solid #ddd !important;
    text-transform: uppercase !important;
    font-weight: bold !important;
    color: #333 !important;
}

#quickLinks li {
    margin: 0px 0px 0px -7px !important;
    min-height: 30px !important;
    background: #f7f7f7 !important;
    border-top: 1px solid #fff !important;
    border-bottom: 1px solid #ccc !important;
}

#quickLinks li a {
    box-sizing: border-box; 
    -moz-box-sizing: border-box;
    padding: 6px 5px 5px 5px !important;
    padding-left: 10px !important;
    display: block;
    width: 100% !important;
    height: 100% !important;
    color: #252525 !important;
    font-size: 13px !important;
    background: none !important;
    text-decoration: none !important;
}

#quickLinks li a:hover {
    background: #eee !important;
}  
/* START OF QUICK LINKS STYLES */

/* BUGFIX! - 1 */
/* STYLING FOR SUGGESTION LIST WHEN REGISTERING */
/* IMPLEMENTED 11/07/13 08:39 AM */
#tat_table{
    visibility: visible; 
    z-index: 10;
    border: 1px solid #aaa !important;
    background: #f9f9f9 !important;
}

#tat_table tr{
    background: #f9f9f9 !important;
    color: #333 !important;
}

#tat_table tr td{
    color: #111 !important;
    text-align: center !important;
}

#tat_table tr td:hover{
    background: #990000 !important;
    color: white !important;
}
/* END OF BUGFIX! - 1 */

.wysiwygtext{
    color: #000;
    font-size: 12px !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
}

#pageTitle{
    margin: -96px 0px 96px -190px !important;
    color: #000 !important;
    z-index: 10 !important;
}

#contextName{
    color: #000;
    font-family: 'Tahoma', sans-serif !important;
    font-size: 40px !important;
    font-weight: 200 !important;
    color: #333 !important;
    position: relative !important;
}

#pageTitleButtons{
    display: none !important;
}

.pHead{
    background: rgba(0,0,0,0.01) !important;
    border-bottom: 1px solid #ccc !important;
    color: #000 !important;
}

.buttonBar{
    background: rgba(0,0,0,0.01) !important;
    border-top: 1px solid #ccc !important;
    color: #000 !important;    
}

.pHead {
    display: block !important;
    width: auto !important;
    height: 100% !important;
    background: #f2f2f2 !important;
    box-shadow: inset 0px 1px 1px white !important;
    border-radius: 5px !important;
    border-bottom-left-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
}

.pHead img{
    margin: 2px -5px 0px 0px !important;
    opacity: 0.5;
    transition: opacity 2s;
}

.pHead img:hover{
    opacity: 1;
}

.pHead a{
    color: #000 !important;
    text-decoration: none !important;
}


.portlet{
    width: auto !important;
    margin: 20px 20px 20px 0px !important;
    border-radius: 3px !important;
    border: 1px solid #d0d0d0 !important;
    background: #fff !important;
    box-shadow: 0px 1px 1px #eee !important;
    transition: border 0.6s;
}

.portlet:hover{
    border: 1px solid #999 !important;
}

.buttonBar {
    box-szing: border-box;
    -moz-box-sizing: border-box;
    padding: 3px !important;
    border-top: none !important;
    background: #f2f2f2 !important;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

#calSideBar {
    box-szing: border-box;
    -moz-box-sizing: border-box;
    padding: 15px !important;
    background: none !important;
}

#calSideBar a{
    color: #333 !important;
}

.buttonBar a{
    display: none !important;
}



#contextPages li{
    background: #fefefe !important;
    border: none !important;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
}



#foot{
    height: 200px;
    margin: 50px auto 0px auto !important;
    box-sizing: border-box;
    -moz-box-sizing: border-box !important;
    padding: 25px 100px 0px 100px !important;
    width: 100% !important;
    box-sizing: border-box;
    background: url(https://raw.github.com/wesleyzloza/myMSOE-Skin/master/footer_bg.png) no-repeat !important;
    background-color: #363636 !important;
    border: none !important;
}

#policies a{
    color: white !important;
    text-decoration: none !important;
}

#policies a:hover{
    text-decoration: underline !important;
}

#copyright{
    background: none !important;
    color: rgba(255,255,255,0.5) !important;
}

/*START OF STYLING TABLES*/

.groupedGrid {
    width: 100% !important;
}

.groupedGrid tr {
    height: 25px !important;
}

.ghead{
    width: 100% !important;
    background: #eee !important;
    color: #252525 !important;
    font-weight: 200 !important;
}

.alt {
    background: #f6f6f6 !important;
}  



}
