css:
@-moz-document url-prefix("http://www.ccs.neu.edu/home/matthias/HtDP2e") {
h1, h2, h3, h4, h5, h6 {
    font-family: 'Bitstream Charter', 'DejaVu Serif', Georgia, serif;
}

body, div, p, ol, ul, li {
    font-family: 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif;
    font-size: .9rem;
	background-color: #F5F5F5;
    color: #151515
}

a:link, a:hover {
  color: inherit;
}

/* Monospace: */
.RktIn, .RktRdr, .RktPn, .RktMeta,
.RktMod, .RktKw, .RktVar, .RktSym,
.RktRes, .RktOut, .RktCmt, .RktVal,
.RktBlk, .stt {
  font-family: 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace;
  font-size: .9rem;
  white-space: inherit;
}

/* Serif: */
.inheritedlbl {
  font-family: 'Bitstream Charter', 'DejaVu Serif', Georgia, serif;
}

/* Sans-serif: */
.RBackgroundLabelInner {
  font-family: 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif;
}
}

@-moz-document url-prefix("http://www.ccs.neu.edu/home/matthias/HtDP2e") {
.navsettop, .tocview, .tocsub { 
  background-color: inherit;
  border: none;
}

.tocsub {
  font-size: .8rem;
}

.RktPn  { color: #505050; }
.RktCmt { color: #505050; }
.RktSym { color: #151515; }
.RktVar { color: #6A9FB5; }
.RktRes { color: #707070; }
.stt    { color: #505050; }
.RktVal { color: #AC4142; }

.refelem, .refcolumn, .refcontent { 
  background-color: inherit; 
  border: none;
  font-family: 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif;
}

.Figure { 
  background-color: #202020;
  border-color: #151515;
  border-radius: 10px !important;
}

.Figure .SIntrapara, .Figure ul, .Figure ol, .Figure li, .Figure p, .Figure td {
  background-color: #202020;
  color: #F5F5F5;
}

.Figure .RktInBg, .Figure .hspace, .Figure .RktIn { 
  background-color: #202020;
}

.Centertext, .Legend, .FigureTarget { 
  background-color: #202020;
  color: #90A959;
}

.RktValLink, .RktStxLink, .techinside, .techoutside { 
  text-decoration: none; 
  color: #AA759F;
  padding: 0 1px;
  background-color: #E0E0E0;
}

.SCodeFlow { 
  padding: 2%; 
  font-size: .8rem;
  background-color: #202020;
  color: #F5F5F5;
  border-radius: 10px !important;
}

.SCodeFlow p {
  background-color: inherit;
  color: inherit;
}

span.highlighted {
/*  background-color: #F4BF75; */
  background-color: #DFAF6C;
}

span.highlighted .RktSym .RktValLink {
  background-color: #B99460 !important;
  color: #F5F5F5 !important;
}

.Figure .RktSym, .RktBlk .RktSym, .SCodeFlow .RktSym { 
  color: #F5F5F5;
}
.Figure .RktSym, .RktBlk .RktSym, .SCodeFlow .RktSym, .refcontent .RktSym,
.Figure .RktVal, .RktBlk .RktVal, .SCodeFlow .RktVal, .refcontent .RktVal,
.Figure .RktVar, .RktBlk .RktVar, .SCodeFlow .RktVar, .refcontent .RktVar,
.Figure .RktCmt, .RktBlk .RktCmt, .SCodeFlow .RktCmt, .refcontent .RktCmt,
.Figure .RktPn,  .RktBlk .RktPn,  .SCodeFlow .RktPn,  .refcontent .RktPn,
.Figure .RktRes, .RktBlk .RktRes, .SCodeFlow .RktRes, .refcontent .RktRes,
.Figure .RktErr, .RktBlk .RktErr, .SCodeFlow .RktErr, .refcontent .RktErr,
.Figure .stt,    .RktBlk .stt,    .SCodeFlow .stt,    .refcontent .stt
{
  font-size: .75rem;
}

.Figure .RktValLink, .Figure .RktStxLink, .Figure .techinside, .Figure .techoutside, .Figure a:link, .Figure a:hover,
.RktBlk .RktValLink, .RktBlk .RktStxLink, .RktBlk .techinside, .RktBlk .techoutside, .RktBlk a:link, .RktBlk a:hover, 
.SCodeFlow .RktValLink, .SCodeFlow .RktStxLink, .SCodeFlow .techinside, .SCodeFlow .techoutside 
{
  background-color: #303030 !important;
}
}
