css:
@-moz-document domain("tweetdeck.twitter.com") {

@media screen and (max-width: 699.999px) {
    #container :not(.js-modal) div.js-chirp-container > article:nth-child(1n - 0) {
        clear: both !important;
    }
    #container :not(.js-modal) div.js-chirp-container > article {
        width: 350px !important;
    }
    section.is-shifted-1 {
        width: 350px !important;
    }
}

@media screen and (min-width: 700px) and (max-width: 1049.999px) {
    #container :not(.js-modal) div.js-chirp-container > article:nth-child(2n - 1) {
        clear: both !important;
    }
    #container :not(.js-modal) div.js-chirp-container > article {
        width: 49% !important;
    }
    section.is-shifted-1 {
        width: 49% !important;
    }
}

@media screen and (min-width: 1050px) and (max-width: 1399.999px) {
    #container :not(.js-modal) div.js-chirp-container > article:nth-child(3n - 2) {
        clear: both !important;
    }
    #container :not(.js-modal) div.js-chirp-container > article {
        width: 32.5% !important;
    }
    section.is-shifted-1 {
        width: 32.5% !important;
    }
}

@media screen and (min-width: 1400px) {
    #container :not(.js-modal) div.js-chirp-container > article:nth-child(4n - 3) {
        clear: both !important;
    }
    #container :not(.js-modal) div.js-chirp-container > article {
        width: 24.5% !important;
    }
    section.is-shifted-1 {
        width: 24.5% !important;
    }
}

#container :not(.js-modal) div.js-chirp-container > article {
    float: left !important;
    border: 1px solid rgba(128, 128, 128, 0.234);
    margin: 1px;
    border-radius: 4px;
}

:not(.js-modal) section.js-column {
    width: 99% !important;
}

.js-detail-container {
    width: 350px;
}
}
