@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("t-mobilebankowe.pl") {
    
     body * {font-family: Arial, Helvetica, sans-serif ;}

    body,html {background-color: black!important;}  
    div.section-bar.wrap.stickit,div.wrap > div.section-bar.stickit, div.stickem-sections > section > div.section-bar.wrap, aside.mobile-nav, .search-box input  {background-color: black!important; color:white !important;}
    div.section-bar.wrap.stickit > h1, div.section-bar.stickit > h1, div.section-bar.wrap > h1 {color:white !important;}
    div.stickem-sections > section:last-child,div.copyright {background-color:black!important;}
    nav.main-nav > ul.main-nav__list > li > a, div.main-nav__sub > div.__group > ul.__sublist > li > a:hover  {color: gold !important;}
    div.wrap > .main-nav {background-image: none !important; background-color: black !important;}
    div.main-nav__sub > div.__group > ul.__sublist > li > a {color: white !important;}
    
    a:hover {color: gold !important;}
    
    
    h1.site-title >a > img ,img#header-logo, img#header-logout-logo,img#header-login-logo {
        
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(https://drive.google.com/uc?export=view&id=0BzFiMflVUxkXOHpxakVxU0puVUk) no-repeat;
  width: 205px; /* Width of new image */
  height: 145px; /* Height of new image */
  padding-left: 205px; /* Equal to width of new image */  
  margin-top: -20px !important; 
        
    }  
}


@-moz-document domain("online.t-mobilebankowe.pl") {  /* stronka logowania*/
    
     body * {font-family: Arial, Helvetica, sans-serif;}

    #mainLoginContainer {background-color: black !important; }
    #headerLoginContainer, #mainLogoutContainer, #headerLogoutContainer, #footerLogoutContainer, #footerLoginContainer {background-color: black !important; color:white !important;}
    div#sec, div#sec > font > ul > li > * {background-color: black !important; color:grey !important;}
    div#sec, div#sec > font > ul {color:#F88017!important;}
    a {color: cyan !important;}
    a:hover {color: gold !important;}
    div#header-login-title {color:#F88017!important;}
    td[class^="alior-button"] {background-color:#F88017!important; background-image:none!important;}
    div#entrust {background-image:none!important;background-color:black!important;}   
    div#antiPhishingImage > img {border: 2px solid black !important; padding: 2px !important;}
    
    
    h1.site-title >a > img ,img#header-logo, img#header-logout-logo,img#header-login-logo {
        
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(https://drive.google.com/uc?export=view&id=0BzFiMflVUxkXOHpxakVxU0puVUk) no-repeat;
  width: 205px; /* Width of new image */
  height: 145px; /* Height of new image */
  padding-left: 205px; /* Equal to width of new image */  
  margin-top: -20px !important;      
    }  


}   


@-moz-document domain("online.t-mobilebankowe.pl") {  /* po zalogowaniu */
    
    
    html  {font-family: Arial, Helvetica, sans-serif;}

    td> #mainContainer, td> #mainContainer >#subMenuContainer , td> #mainContainer > #headerContainer, td> #mainContainer > #mainMenuContainer, td> #mainContainer >#footerContainer, label#customer-label {background-color: black !important; color:orange !important; }
    div#centerContent a {color: gold !important;}
    div[id^="ext-gen"] > span,  div[id^="ext-gen"] > div, td[id^="ext-gen"] > label , span[class^="x-panel-header-text"], span#ext-gen5796, Label#accountOverviewSAATitleLabel  {color: grey !important;}
    div#rightCt > div[id^="ext-comp"]:first-child > div {background-color: black !important; color:orange !important; }
    
    div#c > div[id^="ext-comp"]  {width :171px !important; margin-left: -20px !important;}
    ul.x-menu-list {background-color: grey !important;}
    div#centerContent a.x-btn-text {color: cyan !important;}
    
    h1.site-title >a > img ,img#header-logo, img#header-logout-logo,img#header-login-logo  {
        
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(https://drive.google.com/uc?export=view&id=0BzFiMflVUxkXOHpxakVxU0puVUk) no-repeat;
  width: 205px; /* Width of new image */
  height: 145px; /* Height of new image */
  padding-left: 205px; /* Equal to width of new image */  
  margin-top: -20px !important;      
    }    
        
    tbody.x-btn-small.x-btn-icon-small-left > tr > td.x-btn-mc {background-image:none!important;background-color:orange!important;}   
    /*div#totalPanel *, div#caagrid * {color: black}*/
    div#moneysidebardisplayfield,span.amount-debit, span.accountoverview-availablebalace-pos  {color: green !important; font-weight: bold;}
    div[id^="highcharts"] > * {font-family: Arial, Helvetica, sans-serif;}
    
    div#domesticTransferTransactionalAccodionForm span.amount-debit > span:last-child , div#domesticTransferTransactionalAccodionForm span.miniinfo  {color: green !important;}
    
    div#domesticTransferTransactionalAccodionForm span.amount-debit > span:first-child {color:red !important;}
    .page-loading-bgr,.quick-payment-widget .alior-partnerselector, .quick-payment-widget, .messages-sidebar-messagesform-panel  {background-color:black!important;}

}
