css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.asphalto.org") {

/*  testo  */
body, div, table, textarea {font-family: Verdana, Arial, sans-serif !important; font-size: 9pt !important; color: #000000 !important;}

input {font-family: Verdana, Arial, sans-serif !important; font-size: 7pt !important;}

.quote_text {color: #3D7038 !important; font-size: 7pt !important;}
.quote_author {font-style: italic !important; color: #C57327 !important; font-size: 7pt !important;}

a {text-decoration: none !important; color: #3169B6 !important;}
a:hover {background-color: #3169B6 !important; color:#FCFCFC !important;}

/*  colori, bordi, sfondo  */
#head table {background-color:#D1D1D1 !important;}

body, .collapsed_list_info, .post_info, .ignored_post {background: url(data:image/bmp;base64,Qk1mAAAAAAAAADYAAAAoAAAABAAAAAQAAAABABgAAAAAAAAAAADDDgAAww4AAAAAAAAAAAAA8PDw8PDw8PDw8PDw8PDw8PDw8PDw8PDw7Ozs7Ozs7Ozs7Ozs7Ozs7Ozs7Ozs7Ozs) 0 0 repeat !important;}

body {margin: 0 !important; padding: 0 !important; text-align: center !important; border: none !important;}

input, textarea, select {background-color:#FCFCFC !important; color: #000000 !important; border: 1px #AAAAAA solid !important;}

blockquote {border: 0 !important; border-left: 5px #7fbcfa solid !important; background-color: #B5DAFF !important; color: #000000 !important; margin: 3px 30px 3px 20px !important; padding: 5px !important; font-style: italic !important;}

/* colonne laterali */
.section_title, .section_title_collapsed {background-color:#D1D1D1 !important; border: 1px #AAAAAA solid !important; color: #000000 !important;}

.section_links {background-color: #FCFCFC !important;}

.section_links_info {font-size: 9px !important; font-style: italic !important;}

.section_links_selected {background-color:#7FBCFA !important;}

/* colonna di sinistra */
#left_column .section_links { min-width:126px !important; max-width:126px !important; overflow: hidden !important;}
#left_column .section_links span {color: #000000 !important;}
#left_column .section_links_selected span {color: #000000 !important;}
#left_column .section_links_small {background-color: #FCFCFC !important;}

/* colonna di destra 
#right_column .section_links {max-width:90px !important; overflow: hidden !important; font-size: 7pt !important;}
*/

.unregistered_user {color: #999999 !important;}

/*  elenco thread  */
.collapsed_list {background-color: #FCFCFC !important;}
.collapsed_list, .collapsed_list_info {border: 5px #FCFCFC solid !important;}
.collapsed_list_info {font-size: 8pt !important;}

.collapsed_ignored, .collapsed_ignored_info {background-color:#D1D1D1 !important; font-size: 7pt !important;}

.unread {color: #000000 !important; font-size: 7pt !important; }

.category_description {border: 5px #FCFCFC solid !important; background-color: #FCFCFC !important; font-size: 10pt !important;}

/*  thread  */
.mainpost {background-color: #FCFCFC !important;  border: 3px #FCFCFC solid;}
.main_title {font-size:12pt !important; font-weight:bold !important; background-color: #FCFCFC !important;}
.main_post_text {border: 0 !important; font-size:10pt !important;
background:url(data:image/png;base64,) no-repeat top left !important;}
.main_title {border: 0 !important;}
.main_post_text .signature {border-top:1px #AAAAAA solid !important;font-size: 8pt !important; color:#666666 !important; margin-top:12px !important; padding-top:12px !important;}
.post_info {font-size:8pt !important; color: #000000 !important;}

.reply {background-color: #dcdcdc !important; border: 1px #aaaaaa solid !important; margin: 0 5px 5px 0 !important;}
.reply_title, .reply_post_info {font-size:8pt !important; background-color: #FCFCFC !important; color: #000000 !important;}
.reply_posted_on {color: #000000 !important;}
.forked_reply_title {background-color: #FCFCFC !important; color: #000000 !important;}
.post_text table {position: relative !important; top: -8pt !important;}

.post_text {font-size: 8pt !important; color:#666666 !important; margin-top:12px !important; padding-top:12px !important; background-color:#dcdcdc!important;border: 0 !important;}

.post_text .signature {border-top:1px #AAAAAA solid !important;font-size: 8pt !important; color:#666666 !important; margin-top:12px !important; padding-top:12px !important; background-color:#dcdcdc !important;}

.ignored_post {font-size: 7pt !important;}

.post_form {background-color:#AAAAAA !important;}
.post_form_element {background-color:#D1D1D1 !important;}

/*  homepage  */
.main_post_text_skip_sig {background-color: #FCFCFC !important; border-style: hidden !important;}
.main_post_text_skip_sig .signature {display: none !important;}
.summary {border-style: hidden !important;}
}
