css:
@-moz-document domain("yr.no"){

/** Hovedbakgrunn **/
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video { 
background: #000000;}

/** Tekst **/
.yr-content-body { 
color: #CCCCCC;
margin-bottom: 5px;
padding-bottom: 35px;
}

/** Forecast background **/
.yr-table-overview2 tr:nth-child(2n) td, .yr-table-hourly tr:nth-child(2n) td, .yr-table-longterm-detailed tr:nth-child(2n) td, .yr-table-stats tr:nth-child(2n) td, .yr-table-places tr:nth-child(2n) th, .yr-table-places tr:nth-child(2n) td, .yr-table-records tr:nth-child(2n) td, .yr-table-vacation tr:nth-child(2n) th, .yr-table-vacation tr:nth-child(2n) td, .yr-table-observations tr:nth-child(2n) th, .yr-table-observations tr:nth-child(2n) td, .yr-table-warm-cold-wet tr:nth-child(2n) td, .yr-table-water-temp tr:nth-child(2n) td, .yr-table-special-group tr:nth-child(2n) td { 
background-color: #1A1A1A;
}

/** Table head background **/
.yr-table thead th { 
background-color: #323232;
font-weight: 400;
line-height: 1.1;
padding: 4px 6px;
}

/** Moar background **/
.yr-content-title { 
background: #000000;
border-bottom: 1px solid #CCCCCC;
margin-bottom: 10px;
padding: 20px 0px 0px;
position: relative;
}

/** Warning **/
.yr-info .yr-frame, .yr-warnings .yr-frame { 
background: #ED2727;
border: 5px solid #1A1A1A;
border-radius: 5px;
margin-bottom: 0px;
padding: 4px 10px;
}

/** Left side jpg replacement **/
yr-stickynav-body { 
background: #000000;
padding: 6px 0px;
}


/** Tab bar background **/
.yr-breadcrumbs { 
background: #1A1A1A;
border-bottom: 1px solid #1900FF;
border-top: 1px solid white;
height: 30px;
overflow: hidden;
}

/** Links **/
.yr a { 
color: #546DE9;
}

/** Top tab bar **/
.yr-breadcrumbs a { 
color: #CCCCCC;
text-decoration: none;
}

.yr-breadcrumbs a, .yr-breadcrumbs span { 
background: #1A1A1A;
display: block;
float: left;
padding: 8px 28px 7px 0px;
}

/** Header **/
.yr-header { 
background: 0px 50% transparent;
border-bottom: 1px solid #8CC5D8;
padding-top: 22px;
}

/** Sidebar background **/
.yr-stickynav-body { 
background: #000000;
padding: 6px 0px;
}

.yr-content-body { 
background: #1A1A1A;
color: #CCCCCC;
margin-bottom: 5px;
padding-bottom: 35px;
}

/** Text forecast **/
.yr-content-frame, .yr-content-frame-tight { 
background: #1A1A1A;
}

.yr-textforecast h3 { 
color: #CCCCCC;
font-size: 0.92em;
margin: 11px 0px 0px;
text-transform: uppercase;
}

}
