css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("generation-nt.com") {

    #ctBoc {
    height: 1px;
}

.content {
    margin-top: 45px;
}

.globalcol .centercol, .globalcol .centercol_nobg, .globalcol .centercol_zone {
    width: 100%;
}

.habillage_psvita .content {
    margin-top: 0px;
}

.mainnav {
    margin-top: 5px;
}

aside.sidecol, div.sidecol {
    display: none;
}
     
div.breadcrumbs, div.bup {
    display: none;
}

div.centercol.centercol_right {
    width: 65%;
}

div.global {
    height: 10px;
}

.itemslist .itemlistshortcoms  .itemtitle strong {
    max-width: 800px;
}

.items-list .item-list .item-zone {
    width: 80%;
}

div.similar {
    border-bottom-width: 1px;
    border-color: #c4c2c4;
    border-left-width: 1px;
    border-right-width: 1px;
    border-style: solid;
    border-top-width: 1px;
}

div.toppubs , footer.footer {
    display: none;
}

h1.title {
    margin-top: 2px;
}

header.mainheader {
    height: 62px;
}

ins.adsbygoogle {
    display: none;
}

li.itemlist {
    margin-bottom: 1px;
}

nav.encemoment , span.ico_menu {
    display: none;
}

ul.mainmenu {
    width: 100%;
}

ul.social_btns, ul.socialcount.like.grade-a {
    display: none;
}

.toppubs, .ligatus, .pub300x250, .pub160x600, pub728x90, .pubadsense { 
   display: none !important;
}
    

.items-list .item-list .item-zone {
    width: 80%;
    height: 70px;
}


.items-list .item-list .item-title {
    font-size: 16px;
    margin-top: 5px;
    margin-bottom: 5px;
}
    
a.item-image {
    width: 105px;
    height: 70px;
}

.item-image img {
    width: 105px;
    height: 70px;
}

.items-list .item-list .item-image {
    width: 105px;
    height: 70px;
}

a.item-category {
    top: 0px;
    left: 0px;
}

.item-date {
    margin-left: 150px;
    line-height: 28px;
}

a.item-comments {
    top: 0px;
    left: 650px;
}

.item-list {
    margin-bottom: 10px;
}    

.centercol_right {
    float: left;
}
    
article .description {
    min-height: 100px;
}
    
article .vignette {
    width: 105px;
    height: 75px;
} 
    
article .toolbar_social .keywords {
    margin: 0px 0px 10px !important;
}
    
article .toolbar_social { 
    width:100%; 
    margin-top: 0px;
}
    
.likes-titre, .likes, article .toolbar_social .firstcomm    {
    display:inline; margin: 15px 0px 10px !important;
}
    
article .toolbar_social .coms_link {
    margin: 15px auto 20px;
}
    
.comm_premium { 
    display:none;
}    
    
.submit_follow {
    margin: 00px 10px 0px;
}    

.comm .comm_infos {
    width: 85%;
}
    
}
