css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document domain("packagecontrol.io") {
    header {
        background-image: -moz-linear-gradient(bottom, #1b1b1b 0%, #1b1b1b 100%);
    }
    body {
        background-color: #1b1b1b;
    }
    header form input#search {
        background-color: #353535;
    }
    header nav a {
        color: #CACACA;
    }
    header nav a.active {
        background-color: #b1b1b1;
    }
    ul.packages li:hover {
        background-color: #b1b1b1;
    }
    footer {
        background-color: #1b1b1b;
    }
    kbd, tt, code {
        background-color: #363636;
        text-shadow: none;
    }
    p.code {
        background-color: #363636;
    }
    .code st3 {
        color:#353535;
    }
    body.package div#readme div.contents {
        background-color: #1b1b1b;
    }
    body.package div#readme div.contents code {
        background-color: #363636;
    }
    pre {
        text-shadow: 0 1px 0 #363636;
    }
    body.package div#readme div.contents pre {
        background-color: #363636;
    }
    body.package div#installs div#daily_installs .grid line.minor {
        stroke: #363636;
    }
    body.package div#installs div#daily_installs .grid .major.tick line {
        stroke: #363636;
    }
    .st3, .st2 {
        background-color: #363636;
        color: #a2a2a2;
    }
    ul.packages li .meta .installs {
        background-color: #b1b1b1;
    }
    ul.packages li .meta .installs em {
        color: #1b1b1b;
    }
    body.package div#installs div#daily_installs .layer.windows {
        fill: #677280;
    }
    body.package div#installs div#daily_installs .layer.osx {
        fill: #768494;
    }
    body.package div#installs div#daily_installs .layer.linux {
        fill: #8D9EB3;
    }
}
