 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("code.google.com")
{

body,
#maincol
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
    color: #aaa !important;
}

.phead, div.h4, table.h4, .mainhdr, td, .t5 h4, h3,
.menuDiv, .menuDiv .menuItem,
.menuDiv .menuText {
    background: #111 !important;
}

.menuDiv {
    border-color: #000 !important;
}

.headbg td {
    background: transparent !important;
}

.round1, .round2, .round3, .round4 {
    display: none !important;
}

/* fix home images */

img[src*="https://ssl.gstatic.com/codesite/ph/images/defaultlogo.png"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 64px !important;
    padding-top: 64px !important;
    background: url("https://github.com/inductiveload/stylish-bathys/blob/master/googlecode/Google%20Code64.png?raw=true") no-repeat !important;
}

/* search results */

#serp > table tr > td:first-child {
    min-width: 64px;
    text-align:center;
    padding-left: 5px;
}

#serp > table td {
    background: none !important;
}

/* adv search */
table.advquery, table.issueimport {
     border: none !important;
}

.tip, #peekarea {
    background-color: #222 !important;
}

#peekheading, #infobubble {
    background-color: #111 !important;
    border: 1px solid #000;
}

/* code */
.cuetip, .pmeta_bubble_bg, .popup{
    background-color: #111 !important;
    border-color: #000 !important;
}

.pmeta_bubble_bg, #controls a.revchoose, .popup  {
    border: 1px solid #000 !important;
    padding: 5px !important;
}

#browsebox {
     border-right: 1px solid black !important;
     border-left: 1px solid black !important;
     border-bottom: 1px solid black !important;
}

#filelist, #review_nav {
    background-color: #222 !important;
}

#dirlist, #dirlist tr td {
    background-color: #181818 !important;
}

#dirlist {
     border-right: 1px solid black !important;
}

#filelist {
     border-left: none !important;
}

.selected {
    background-color: #111 !important;
}

#dirlist li ul {
     border-top: 1px solid black !important;
}

.fc #lines {
    border-left: 3px solid #000 !important;
}

/* diffs */

.diff {
    color: #ddd !important;
}

.diff .newadd {
    background-color: #224422 !important;
}
.diff .newbackadd {
    border-color: #224422 !important;
}

.diff .neweq {
    background-color: #448844 !important;
}
.diff .newbackeq {
    border-color: #448844 !important;
}

.diff .oldrm {
    background-color: #422 !important;
}
.diff .oldbackrm {
    border-color: #422 !important;
}

.diff .oldeq {
    background-color: #844 !important;
}
.diff .oldbackeq {
    border-color: #844 !important;
}

/* issues */
.updates {
    background-color: #222 !important;
}

#issuemeta {
     border-right: 2px solid black !important;
}

#issueheader {
     border: 2px solid #111 !important;
}

#issueheader td {
    background: #111 !important;
}

.role_label {
    background: #003 !important;
}

/* wiki */
#wikipage {
    background: #222 !important;
}

#wikimaincol {
    background: #111 !important;
    border: 1px solid black !important;
}

.issuecomment, #review_nav, .attachments,
.artifactcomment, #commentform {
     border-top: 2px solid black !important;
}

.subt {
    background: #333 !important;
    border-bottom: 1px solid black !important;
}

.gtb {
    background: #111 !important;
    border-bottom: 1px solid #888 !important;
}

.gtb .active {
    background: #333 !important;
    border-color: #000 !important;
}

/* links */
a:link, a:focus, .popup td {
    color: #00ADEE !important;
}

a:visited {
    color: #55ADAA !important;
}

/* project name link */
#pname a {
    color: #bbb !important;
}

/* inputs */
input[type="text"], select, textarea,
input[type="button"], input[type="submit"], .buttonify{
    background: #111 !important;
    border: 1px solid #000 !important;
    color: #aaa !important;
}

/* code areas */
#wikicontent pre.prettyprint,
#wikipreview pre.prettyprint
{
    background: #161616 !important;
}

/* code formatting*/
.prettyprint span.pln, .prettyprint span.pun {
    color: #D8D8D8 !important;
}

.prettyprint span.kwd, .prettyprint span.atv  {
    color: #FFFFFF !important;
}

.prettyprint span.str{
    color: #FFC600 !important;
}

.prettyprint span.tag {
    color: #FF0000 !important;
}

.prettyprint span.lit {
    color: #7FB347 !important;
}

.prettyprint span.com {
    color: #C7DD0C !important;
}

.prettyprint span.typ {
    color: #D25252 !important;
}

/* download list */
.results {
    border-collapse: collapse !important;
}

.results th, .results_lite th,
.list-foot, .list {
    background-color: #333 !important;
}

.results tr td {
    background-color: #222 !important;
}

.results, .results td, .results th,
.results_lite th,
.list, .list-foot {
    border-color: #000 !important;
}

/* wiki */
.fc, .pending_bubble, .CodeMirror-wrapping {
    background-color: #222 !important;
}

.editbox {
    border-color: #000 !important;
}

.CodeMirror-line-numbers {
    border-color: #000 !important;
}

}
