css:
/*=====================
    Important Stuff
=====================*/
@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {

/*=======================================================================

============================
===============================================Global======================

===========================
=========================================================================

===========================*/

.post_avatar, .post_avatar_link, .search_form_row { background-color: transparent!important; }

.controls_section.user_list li a:before { content: none!important; }

.right_column:after { content: none!important; }

a.radar_avatar:before { content: none!important; }

.post_permalink { 
  	height: 16px!important;
    width: 16px!important;
	margin: 5px;
    border-radius: 5px!important;
	background-color: #ddd!important;
  	background-image: none!important;
}

.post_permalink:before {
  	top: 0px!important;
    right: 0px!important;
    content: none!important;
    border: none!important;
}

.follow { color: inherit!important; }

#dashboard_controls a, #dashboard_controls .control {
  	color: #eee!important;
  	text-shadow: #333 1px 1px 1px!important;
}

#dashboard_controls a, .controls_section a .count canvas { 
  	-webkit-filter: grayscale(100%);
	-moz-filter: grayscale(100%);
	filter: grayscale(100%);
}

div.l-header .selection_nipple { border-bottom-color: inherit; }

.search_form_row  {
background: rgba(0, 0, 0, 0.04) !important;
color: #000 !important;}

#pagination a { color: #ccc!important; }
#pagination a:hover { color: #ddd!important; }

.footer_links a { color: #999!important; }


#logo {height: 0 !important; width: 0 !important; padding-left: 320px !important; padding-top: 110px !important; background: url('http://static.tumblr.com/eixvjel/Vm1n562sa/logo.png') no-repeat !important;}

div.l-header .tab.iconic a{
  background-color: rgba(0, 0, 0, 0.04) !important;
  border-radius: 10px !important;}


/*==========================================Body 

background==========================================*/

body 
	{background: url('http://imagizer.imageshack.us/a/img838/4975/34gl.png') center #87bec8 repeat fixed 

!important;}  
*  
	{text-shadow:none !important;}
	
/* ====================Solid content area in front of background, behind content 

bubbles=======================*/

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0, 0, 0, 0.04) !important;
}
	
/*===============================Load More Posts 

Banner================================================*/

#auto_pagination_loader
	{background: rgba(0, 0, 0, 0.04)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .5 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	
/*=======================================Selection Nipple 

================================================*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px transparent !important;}	
	
/*=======================================Right Column 

Highligts================================================ */
	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: rgba(0, 0, 0, 0.04) !important; 
	background: #ffffff !important;}

/*=======================================================================

=================================
===============================================Dashboard===================

===============================
=========================================================================

================================*/	
	
/*==============================Right Column Tags (Dashboard and Tagged Pages)

==============================*/	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color: rgba(0, 0, 0, 0.04) !important;  
	background-image:none !important;
	border:0px !important}

div.hide_overflow
	{font-size:.9em;}
a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:8px;}
a.tag
	{position:relative;top:-4px;}
.selected  .tag
	{height:14px;background:#ffffff !important; 
	position:relative;top:0px;
	color:rgba(0, 0, 0, 0.04) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #ffffff !important;}
	
/*===========================================Right Column Radar 

Image============================================*/

div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color: rgba(255, 255, 255, 0.75) !important;
	opacity:.6; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #ffffff !important;}

/*========================================Blockquotes are crazy! 

==============================================*/

blockquote
	{margin-left:-5px !important;}

/*=======================================================================

====================
=======================================Log In 

Page============================================
=========================================================================

====================*/

.login.sign_up,
A[href="/forgot_password"] {display: none !important;}	

/*=======================================================================

====================
===================================== Text Post 

Screen=========================================
=========================================================================

====================*/

.mceContentBody {
  background: none !important; }
  
/*==================================================
Notification Bubbles and other stuff I'm not messing with. :)
====================================================*/

ol#posts li.notification
	{background-color: #3e3e40 !important;
	border-bottom: 2px solid #333333 !important;
	color: #000000 !important;}

#right_column:after { 
display: none !important;}

#right_column a {
color:rgba(255, 255, 255, 0.75)!important;}

#right_column .controls_section li a {
  color:rgba(255, 255, 255, 0.75) !important; /*(0,0,0, 0.75)(20, 20, 20, 0.84)*/}

#right_column .controls_section li a .count {

  color: rgba(255, 255, 255, 0.75) !important;}

	
#left_column #posts .post .permalink
	{background-color:#3e3e40;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#3e3e40 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

.notification_type_icon
	{display:none !important}

#left_column #posts .notification 
	{background-color:#3e3e40 !important;
	color:#666 !important;
	border-bottom-color: #eee !important}

#new_post_notice_container
	{color:#fff !important}

#new_post_notice_container a
	{color:#fff !important}

.new_post_label/*_icon*/ > I {display: none !important}
.new_post_label {
  font-size:0px !important;
  background-clip: content-box;
  padding: 77px 1px 2px 0 !important;
}
.new_post_label:hover {
  color: black !important;
  background-color: rgba(000,000,000,.0) !important;
}

.new_post_buttons {
  background: white center no-repeat url('http://static.tumblr.com/eixvjel/oS5n55zpj/icons.png') !important;
}

/*=====================
    Important Stuff
=====================*/	
	
}
