css:
@-moz-document domain(hetnieuweinstituut.nl) {

#header {height:auto}
.homepage-block > .inner-container .container:before{border:none}

#block-menu-block-baseline-main-menu{width:75%}
#block-menu-block-baseline-main-menu .leaf{float:left; margin-right:20px}
#block-locale-language{width:12.5%;float:right}
#block-locale-language a{font-size:15px}
#block-search-form{width:12.5%;float:right}
.block-search{clear:none}
#header .block {width:auto}

.homepage-block > .inner-container > h2{
    background-color: #000000;
    height: 75px;
    margin:50px 0;
    }

#sidebar-first .title-wrapper:after{border-left:none}
.content-type{display:none; visibility:hidden}
.views-row-container{text-align:left;height:auto}
.content-column span{display:inline; color:#444}
.content-block .inner-container span{background-color:#eee;padding:5px}
.homepage-block > .inner-container > h2{display:none}
#content{margin-top:100px}

.event-group .date-label {color:#ccc}

.content-column{width:auto;float:right}
.homepage-column-projects{width:75%}
.homepage-column-projects img {width:100%; height:auto!important}
.homepage-column-events{width:25%}
.homepage-column-events .date-label{width:100%;font-size:36px}
.homepage-column-events .inner-section{width:100%}
.one-sidebar.front #content{margin-top:0}

.content-column .inner-container{}
.large{font-size:15px}


.node-type-news .page-container::before, .event-past .page-container::before, .node-type-blog .page-container::before, .node-type-press-release .page-container::before,
.node-type-page.submenu .inner-wrapper::after,
#block-hnitree-hnitree-siblings .content::before,
#block-hnitree-hnitree-sidebar::after,
.node-type-news .block-system, .event-past .block-system, .node-type-blog .block-system, .node-type-press-release .block-system,
.node-type-page .inner-wrapper::before, .event-current .inner-wrapper::before, .node-type-project .inner-wrapper::before, .node-type-webform .inner-wrapper::before,
#sliding-popup .popup-content #popup-buttons button:hover, #sliding-popup .popup-content #popup-buttons button:focus, #sliding-popup .popup-content #popup-buttons button:active,
input[type="submit"],
.node-type-news .views-row.active, .event-past .views-row.active, .node-type-blog .views-row.active, .node-type-press-release .views-row.active{background-color:#fff}

.node-type-page.submenu .inner-wrapper:after{background-color:#ddd}


}
