css:
@-moz-document domain("car-inspection.sakura.ne.jp"){
/*=================================================
  Description Of The Car Inspection! ja jp liquid compact 2014/05
  domain('car-inspection.sakura.ne.jp')
 ==================================================*/

/*--- unwanteds ---*/
.area_uti img[alt="車検費用一括見積もり"],
.area_uti img[alt="車検費用一括見積もり"]+table,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}
body {margin: 0 !important; font-family: inhert !important;}
#container, #greywaku {width: auto !important;}


/*--- header/hooter ---*/
#header table {width: 100% !important;}
#header a img {max-width: 100% !important;}
#footer {width: auto !important; font-size: medium !important;}
#footer p {margin: 0 !important;}

/*--- main contents ---*/
#contents {width: auto !important;}
#main {width: 100% !important; margin: 0 0 0 -190pt !important;}
#main>* {width: auto !important; margin: 0 0 0 190pt !important;}
#main>* div {width: auto !important;}
#sub {width: 190pt !important;}

/*--- in right (main) ---*/
#main .area_soto {padding: 0.5em !important;}
#main .area_uti {padding: 0 0.5em !important;}
#main .area_uti h3{height: auto !important; background-size: 100% auto !important;}
#main .area_uti h3+p+div>img{height:3em !important; float: left;}
#main .area_uti h3+p+div>br{display: none !important;}
#main .area_uti h3+p+div>div {position: static !important; height: auto !important;}
#main .area_uti h3+p+div+* {clear: both;}
#main>* p {margin: 0 0 0.5em 0 !important; text-indent: 1em;}
#main table {width: 100% !important;}


/*--- in left (sub) ---*/
#sub .inner {padding: 0 0.5em !important;}
#sub [class^="menu"] ,
[class^="mouse"], [class^="mouse"] a {height: auto !important;}
}
