/* i really want this to be global *//* i really want this to be global */

#header-row{
background: #252526 !important;
}

.tree-drag-tile.folder>.node-link>.node-content, .tree-view .folder>.node-link>.node-content, .header-post11 .hub-groups-section li a
{
	color:#dcdcdc ;
}

.leftPane
{
	background: #252526;
}



.header-post11 #navWrapper
{
	background: #252526;
}


.rightPane, #taskboard
{
	background:#1e1e1e;
}

.taskboard-cell
{
	border-color:#3f3f46 !important;
  
}

td[axis="taskboard-table_s0"] {
 background-color:  rgba(174,208,255,0.5) !important;
}

td[axis="taskboard-table_s1"] {
 background-color: rgba(163,220,163,0.5) !important;
}


td[axis="taskboard-table_s2"] {
 background-color: rgba(255,253,120,0.6) !important;
}

td[axis="taskboard-table_s3"] {
 background-color: rgba(247,226,255,0.5) !important;
}


td[axis="taskboard-table_s5"] {
 background-color: rgba(255,198,198,0.5) !important;
}

.sprint-title-right-container
{
	border-left: 1px #3f3f46 solid;
    border-right: 1px #3f3f46 solid;
   border-bottom: 1px #3f3f46 solid;
  padding-bottom:5px;
  -webkit-border-bottom-left-radius: 10px;
-moz-border-radius-bottomleft: 10px;
border-bottom-left-radius: 10px;
}

.tbTileContent
{
	text-align:center;
  border-left:4px solid #f2cb1d !important;

-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;

  background-color:#1e1e1e !important;
	opacity:0.9;
}

.tbTileContent:hover
{
	border: 2px solid #b8d6fb !important;
}

.onTileEditTextDiv 
{
	font-weight: bold !important;
}

.witState
{
	font-size:130% !important;
  color:white !important;
}

tbody .taskboard-cell{
	padding-bottom: 1px !important;
  	background:#1e1e1e;
}

.taskboard-parent
{
	background:#1e1e1e;   
}





.hub-title
{

  color:white;
  
}



.vc-page-title, .ui-tabs .ui-tabs-nav li a, .witTitle , thead, .info-text, .message-content, .discussion-host.ui-tabs-panel.ui-widget-content.ui-corner-bottom
{
 color:#dcdcdc !important;
}

.handleBar, .group-header
{
	background: #252526 !important;
   color:#dcdcdc;
}

.tree-view>ul.tree-children li.selected>a>div.node-content, .tree-view.focus>ul.tree-children li.selected>a>div.node-content, li.tree-drag-tile>a>div.node-content, .tree-view>ul.tree-children li.focus>a>div.node-content, .tree-view>ul.tree-children li.hover>a>div.node-content {
  background-color: #3f3f46 !important;
  	
  }
  
.hub-pivot-content, .grid-gutter
  {
  	 background-color: #1e1e1e !important;	
  }
  
  .grid-gutter-row-normal
{
	background-color: #1e1e1e !important;
    border: none !important;
}

.content-section
{
background-color: #1e1e1e !important;	
}

.cell-title, .sprint-summary-tile-sprint-dates, .grid-cell, .work-item-view legend
{
 color:#dcdcdc;
}

.ui-dialog{
  background-color: #1e1e1e  !important;
}
  
.headers.ui-tabs-nav.ui-helper-reset.ui-helper-clearfix.ui-widget-header.ui-corner-all, .ui-tabs .ui-tabs-nav
{
    background-color: #3f3f46  !important;

}

#navWrapper
{
	border-bottom:1px #3f3f46 solid !important;
}

.hub-splitter.splitter.horizontal>.leftPane
{
		border-right: 0;
}



.header-post11 .hubs li.selected a
{
	color:#b8d6fb !important;
  border-bottom: 5px solid #b8d6fb;
}

.content-section a , .header-post11 .hub-groups-section li.selected a, .pivot-view li.selected a, .node-link, .change-link , .ui-tabs .ui-tabs-nav li.ui-tabs-active a
{
		color:#b8d6fb !important;
}

.grid-row.grid-row-normal:hover, .grid-row-selected-blur, .grid-row-current.grid-row-selected
{
	background-color:#3f3f46;
    border: 1px solid #b8d6fb !important;
}

.taskboard-cell.ui-droppable:hover
{
	opacity:1;
}

.taskboard-cell.ui-droppable
{
	opacity:0.9;
}
