css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://en.wikipedia.org/") {
   @font-face {
        font-family: 'Open Sans';
        font-style: normal;
        font-weight: 700;
        src: local('Open Sans Bold'), local('OpenSans-Bold'), url(http://themes.googleusercontent.com/static/fonts/opensans/v8/k3k702ZOKiLJc3WVjuplzKRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
    
    @font-face {
        font-family: 'Open Sans';
        font-style: normal;
        font-weight: 400;
        src: local('Open Sans'), local('OpenSans'), url(http://themes.googleusercontent.com/static/fonts/opensans/v8/cJZKeOuBrn4kERxqtaUH3bO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}
    
    @font-face {
  font-family: 'Merriweather';
  font-style: normal;
  font-weight: 400;
  src: local('Merriweather'), url(http://themes.googleusercontent.com/static/fonts/merriweather/v6/RFda8w1V0eDZheqfcyQ4EKRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
    
    body {
        font-family: 'Open Sans' !important;
        font-weight: 400;
        text-rendering: optimizelegibility !important;
    }
    
    a {
        color: #3498db !important;
        outline: none;
    }
    
    div#mw-panel a {
        color: #888 !important;
    }
    
    div.vectorTabs {
        background-image: none;
        opacity: 0.5;
        transition: all 0.3s ease-in-out;
    }
    
    div.vectorTabs:hover {
        opacity: 1;
    }
    
    div.vectorTabs ul {
        background: none;
        margin-left: 15px;
    }
    
    div.vectorTabs span {
        background: none;
    }
    
    div.vectorTabs ul li {
        background: none;
    }
    
    div.vectorTabs ul li a {
        border: 1px solid #65BCF7;
        padding: 7px 25px 2px;
        border-radius: 50px;
        margin: 0 3px;
        text-decoration: none;
    }
    
    div.vectorTabs li.selected a {
        background-color: #3498db;
        border: 1px solid #3498db;
        color: #fff !important;
    }
    
    div#simpleSearch {
        background-image: none;
        border: 1px solid #65BCF7;
        border-radius: 50px;
        padding: 5px;
        margin-top: -6px;
        opacity: 0.5;
        transition: all 0.3s ease-in-out
    }
    
    div#simpleSearch:hover, div#simpleSearch:focus, div#simpleSearch:active {
        opacity: 1;
    }
    
    div#simpleSearch #searchInput {
        padding: 0.6em 0 0.2em 1em;
    }
    
    div#simpleSearch input {
        color: #65BCF7 !important;
    }
    
    div#simpleSearch #searchButton, div#simpleSearch #mw-searchButton {
        right: 7px;
    }
    
    div.vectorTabs li.selected {
        background-image: none;
    }
    
    div#content {
        border: none !important;
    }
    div#content h1 {
        font-family: 'Open Sans' !important;
        font-weight: 700;
        letter-spacing: -0.02em;
        color: #222;
        border-bottom: none;
        font-size: 2.3em !important;
    }
    
    .mw-body-content {
        font-family: 'Merriweather' !important;
        font-size: 0.88em !important;
        line-height: 1.65 !important;
        color: #333;
        margin-top: -10px
    }
    
    #siteSub {
        font-family: 'Open Sans' !important;
        font-weight: 400;
        color: #aaa;
        letter-spacing: -0.01em;
        font-size: 100% !important;
        position: relative;
        top: 5px;
    }
    
    #mw-head-base {
        background: #fff;
        padding-bottom: 20px;
    }
    
    #mw-page-base {
        background-image: none !important;
        background-color: #f6f6f6 !important;
    }
    
    div#content h2 span.mw-headline {
        font-family: 'Open Sans' !important;
        font-weight: 700;
    }
    
    div#content h2 {
        border-bottom: 1px solid #ddd !important;
        padding-bottom: 10px;
        font-size: 1.7em;
        letter-spacing: -0.01em;
    }
    
    #toc, .toc, .mw-warning {
        font-family: 'Open Sans' !important;
        border: 1px solid #ddd !important;
        background: #fcfcfc !important;
        margin-top: 2em;
        padding: 1.5em;
        border-radius: 3px;
    }
    
    .infobox, body.mediawiki table.mbox-small, div.thumbinner {
        font-family: 'Open Sans' !important;
        border: 1px solid #ddd !important;
        background: #fcfcfc !important;
        padding: 3px;
        border-radius: 3px;
    }
    
    .catlinks {
        font-family: 'Open Sans' !important;
        border: 1px solid #ddd !important;
        background: #fcfcfc !important;
        padding: 5px 15px 10px;
        border-radius: 3px;
        font-size: 90%
    }
    
    ol.references, div.reflist, div.refbegin {
        font-family: 'Open Sans' !important;
    }
    
    sup.reference {
        font-family: 'Open Sans' !important;
        line-height: 0.2
    }
}
