css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blognone.com") {
    @media only screen 
    and (min-device-width : 320px) 
    and (max-device-width : 480px) {
        body {
            width: 480px !important;
        }

        #header {
            overflow: hidden;
        }
        
        #header-content {
            max-width: 100%;
        }
        
        #logo {
            text-align: center;
            float: none;
            width: auto;
        }
        
        #navi-menu li {
            display: none;
        }
        
        #navi-menu li:nth-child(1),
        #navi-menu li:nth-child(2) {
            display: inherit;
        }
        
        #content {
            width: auto;
        }
        
        #main-content {
            width: auto;
            margin: 0;
            float: none;
        }
        
        #main-content .content-title-box {
            border-radius: 0;
        }
        
        #main-content .node {
            border-radius: 0;
        }
        
        #main-content .content img {
            width: 100%;
            height: auto;
        }
                
        #sidebar {
            float: none;
            padding-top: 30px;
            width: auto;
        }

        #sidebar .block {
            border-radius: 0;
        }
        
        #righttop_sidebar .content {
            padding: 0;
        }
        
        #rightbottom_sidebar {
            border-radius: 0;
        }
        
       #footer-content {
           width: auto;
       }
        
       #footer-content .block:nth-child(1){
           display: inherit;
           margin-left: 10px;
       }
        
       #footer-content .block:nth-child(1) li{
           list-style-type: none;
       }        
        
       #footer-content .block{
           display: none;
       }        
    }
}
