css:
@-moz-document url-prefix("http://feedly.com/"), url-prefix("https://feedly.com/"), url-prefix("https://cloud.feedly.com"), url-prefix("http://cloud.feedly.com") {
.u5Entry.textOnly .title {
padding-top: 17px;
padding-left: 17px;
padding-right: 17px;
margin-left: -17px;
margin-right: -17px;
margin-top: -17px;
display: block;
height: 160px;
overflow: hidden;
margin-bottom: 10px;
border-top-right-radius: 5px;
border-top-left-radius: 5px;
font-size: 14px;
line-height: 20px;
font-weight: normal;
font-family: 'roboto slab';
text-rendering: optimizeLegibility;
}


.u5Entry .title {
display: block;
margin-bottom: 16px;
height: 32px;
overflow: hidden;
letter-spacing: -0.01em;
font-size: 14px;
line-height: 16px;
font-weight: normal;
font-family: 'roboto slab';
text-rendering: optimizeLegibility;
}


BODY {
margin: 0px;
font-size: 12px;
font-family: 'roboto slab';
}


.inlineFrame .u100entry .title {
font-size: 24px;
line-height: 28px;
font-weight: normal;
font-family: roboto slab;
text-rendering: optimizeLegibility;
}

.entryBody h3 {
font-size: 16px;
font-weight: normal;
font-family: roboto slab;
text-rendering: optimizeLegibility;
line-height: 1.15em;
text-transform: none;
color: #333333;
clear: both;
margin: 1.7em 0 0.7em 0;
letter-spacing: -0.04em;
/* font-weight: bold; */
}


.entryBody {
color: #333333;
font-size: 14px;
line-height: 1.7;
margin-top: 17px;
margin-bottom: 0px;
max-width: 700px;
font-family: roboto slab;
}


.entryBody a {
color: inherit;
text-decoration: none;
font-weight: normal;
border-bottom: 1px dotted #D5D5D5;
color: rgb(42, 165, 42);
}


.entryBody a:visited {
color: inherit;
text-decoration: none;
font-weight: normal;
border-bottom: 1px solid #666;
color: rgb(138, 209, 138);
}


.inlineFrame .entryBody img {
border: 0px;
padding: 0px;
border-radius: 7.5px;
}
}
