css:
@-moz-document domain("vkdownload.net") {
#sideDroite .activeMenu, #sideGauche .activeMenu {
background-color: #4CF5F5;
background-image: -webkit-gradient( linear, left bottom, left top, color-stop(0.17, #60C5FD), color-stop(0.8, #006DB4) );
background-image: -moz-linear-gradient( center bottom, #2B6E23 17%, #4B9441 80% );
}
#sideDroite .activeMenu a, #sideGauche .activeMenu a {
color: #fff;
text-shadow: 1px 1px 0px #214D7E;
}
h1 a {
display: block;
background: url('http://i58.servimg.com/u/f58/18/06/99/75/vklogo11.png');
height: 86px;
text-indent: -1000px;
cursor: pointer;
}
  .etatCursor {
display: block;
width: 50%;
background-color: #D53131;
height: 100%;
}
    #suggestSection{
        display:none;
        position:absolute;
        background-color:#fff;
        border:1px solid #909090;
        border-radius: 5px;
        margin-top:0px;
        margin-left:10px;
        width:310px;
        box-shadow: 0 0 5px rgba(0, 0, 0, 0.17);
    }
    #suggestSection:after,#suggestSection:before {
        bottom: 100%;
        border: solid transparent;
        content: " ";
        height: 0;
        width: 0;
        position: absolute;
        pointer-events: none;
    }

    #suggestSection:after {
        border-color: rgba(255, 255, 255, 0);
        border-bottom-color: #fff;
        border-width: 8px;
        left: 50%;
        margin-left: -8px;
    }
    #suggestSection:before {
        border-color: rgba(144, 144, 144, 0);
        border-bottom-color: #909090;
        border-width: 9px;
        left: 50%;
        margin-left: -9px;
    }
    #suggestSection a, #suggestSection li.title {
        padding:4px;
        display:block;
        font-size:13px;
        text-decoration:none;
        line-height: 26px;
    }
    #suggestSection li.title {
        font-weight: bold;
        color: #2C7A22;
        border-bottom: 2px solid #0080C0;
    }
    #suggestSection li img {
        vertical-align: middle;
        padding-right:5px;
    }
    #suggestSection a.active {
        background-color:#E4E4E4;
    }
table tbody tr.active {
background-color: #C00000;
}
#sideDroite .activeMenu, #sideGauche .activeMenu {
background-color: #C00000;
background-image: -webkit-gradient( linear, left bottom, left top, color-stop(0.17, #C40202), color-stop(0.8, #FF0505) );
}
  #sideDroite .activeMenu a, #sideGauche .activeMenu a {
color: #fff;
text-shadow: 1px 1px 0px #333;
}

#sideDroite .activeMenu a, #sideGauche .activeMenu a {
color: #fff;
text-shadow: 1px 1px 0px #333;
border-top: 1px solid #751B1B;
border-bottom: 1px solid #751B1B;
}

h1, h2, h3, h4, h5, h6 {
font-size: 100%;
font-weight: normal;
}
table tbody tr.active td {
border-left: 1px solid #944141;
color: #fff;
}
#resultSearch h3 a {
font-size: 16px;
color: #0248C4;
text-decoration: none;
font-weight: bold;
}
#menuSideBlock {
overflow: auto;
}
#resultSearch .link a {
color: #0248C4;
font-size: 13px;
padding: 10px;
}
}
