css:
@-moz-document domain("play.spotify.com"), domain("spapps.co") {
/* GENERAL */

body, html { 
    background: #444 !important;
}
a {
    outline: 0 !important;
}
h1.main-header { /* Discover & Messages header bit more right */
    padding-left: 7px !important;
}
#header h1 { /* Header for Browse & Follow */
    padding-left: 7px !important;
}
#main>div.stacked>div .overlay { /* Tabs in the background overlay */
    background: rgba(18,19,20,0.8)
}
.section-divider>* { /* All kind of sub-headlines: artist page, browse,... */
    color: #bbb !important;
}
#notification-area>div { /* Warning pop up when session is interrupted from another playback */
    background: #10780C !important;
    color: #ddd !important;
}
  
/* Navbars */
.navbar { /* All kind of navbars: Browse, Following, Your Music */
    background: #343434 !important;
}
.nav a { /* Non-selected headlines */
    color: #bbb !important;
}
.nav > li.active > a, .nav > li:hover > a {
    color: #eee !important;
}
.nav > li:first-child {
   margin-left: 5px !important;
}


/* Info-boxes */
.media-info { /* All info boxes e.g. Discover, Browse, Search results, etc */
    background: #303030 !important;
    border-bottom-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
    text-align: center !important;
}
.media-info .media-title>span { /* Bigger font in info-boxes */
    font-size: 1.1em !important;
}
.text-muted a, a.text-muted { /* 2nd line in info-box */
    font-size: 1.1em !important;
    color: #aaa !important;
}
.text-meta { /* Followers in info-box */
    color: #aaa !important;
}
.ellipsis { /* Doesn't display a fade out at info boxes when title too long in 2nd line */
    background: transparent !important;
}

/* Song-lists: playlists , Your Music songs */
.tl-cell { /* All kind of song lists: playlists, songs, etc */
    background: #3F3F3F !important;
    box-shadow: 0px 0 0px #3D3D3D,  0px 0 1px #3D3D3D !important;
}
.tl-row:hover .tl-cell { /* Hovering over a row */
    background: #555 !important;
    box-shadow: 0px 0 0px #545454,  0px 0 1px #545454 !important;
}
.tl-row.selected .tl-cell {
    background: #595959 !important;
    box-shadow: 0px 0 0px #585858,  0px 0 1px #585858 !important;
}
.tl-row.playing .tl-cell, .tl-row.paused .tl-cell {
    background: #3C3C3C !important;
    box-shadow: 0px 0 0px #3D3D3D,  0px 0 1px #3D3D3D !important;
}
.tl-row.playing.selected .tl-cell, .tl-row.paused.selected .tl-cell {
    background: #545454 !important;
    box-shadow: 0px 0 0px #535353,  0px 0 1px #535353 !important;
}
.tl-row.playing:hover .tl-cell, .tl-row.paused:hover .tl-cell {
    background: #515151 !important;
    box-shadow: 0px 0 0px #505050,  0px 0 1px #505050 !important;
}
.tl-row.playing .tl-cell, .tl-row.playing .tl-cell a:link, .tl-row.playing .tl-highlight, .tl-row.playing .tl-cell a:visited, .tl-row.paused .tl-cell, .tl-row.paused .tl-cell a:link, .tl-row.paused .tl-highlight, .tl-row.paused .tl-cell a:visited {
    color: #7BB000 !important;
}
.tracklist-album, .tracklist-dev, .tracklist-messages, .tracklist-playlist, .tracklist-popular, .tracklist-trackset, .tracklist-search { /* Song length and column headlines above list */
    color: #ccc !important;
}
.tl-row.unavailable .tl-featuring a, .tl-row.unavailable .tl-highlight { /* Unavailable tracks */
    color: #777 !important;
}
.dropdown-interior-menu a{ /* Drop down menu "..." */
    background: #393939 !important;
    color: #ccc !important;
    border: 1px solid #353535 !important;
}

.dropdown-interior-menu > li > a:hover {
    background: #474747 !important;
    color: #fff !important;
}

.tl-explicit .label { /* Explicit tags */
    color: #888 !important;
}


/* SIDEBAR */

.main-menu.narrow-menu { /* Whole sidebar */
    background: #303030 !important;
}
.main-menu.narrow-menu a { /* Text */
    color: #ccc !important;
}
.main-menu.narrow-menu a:hover { 
    color: #fff !important;
}
.main-menu.narrow-menu a.active, .main-menu.narrow-menu a.active:hover { /* Aktives Element */
    color: #84BD00 !important;
    background: #272727 !important;
}


/* NOW PLAYING RIGHT SIDE */

#wrap { /* Top part */
    background: #333 !important;
}
#bar-outer { /* Song length slider */
    background: #777 !important;
}
#track-current, #track-length {  /* Time */
    color: #aaa !important;
}
#suggestions { /* When queue ends , song suggestions for radio in now-playing screen */
    background: #333 !important;
    color: #ccc !important;
}
#suggestions ul li:hover {
    background: #404040 !important;
}
/**
#controls .playback { 
    margin-top: -7px !important;
}
#controls .extra { 
    margin-top: -13px !important;
}
**/


#controls .extra { /* background of that new stripe under controls */
    margin-bottom: 60px !important;
}

#vol-bar-inner { /* Green volume bar */
    background: #7EB500 !important;
}

/* Bottom part */
#related-music-view {
    background: #353535 !important;
}
#related-music-view .section-divider { /* Related music divider */
    background: #3f3f3f !important;
    border-bottom: 2px solid #202020 !important;
}
#related-music-view .mo-info, .media.horizontal.small .media-wrapper .media-info { /* Info boxes related: First Chrome, 2nd FireFox... */
    background: #494949 !important;
}
.media-object { /* Making the info boxes brighter */
    opacity: 0.8 !important;
}
.media-object:hover {
    opacity: 1 !important;
}
.media.horizontal.small .media-wrapper .media-info .media-title > span, .mo-title { /* 1. Line bit brighter */
    color: #fff !important;
}
/* Install desktop client window */
#desktop .content.show {
    background-color: #373737 !important;
}


/* QUEUE */

.list-playlist tr.playing .list-item-hl, .list-playlist tr.playing td a, .list-playlist tr.playing td.list-row-time { /* Greener for current playing track */
    color: #7BB000 !important;
}
tr.current.paused.selected.selection-focus:hover .list-row-controls button { /* First line time & options */
    background-color: #595959 !important;
}
.table.list-playlist tr { /* Headers, numbers, time */
    color: #bbb !important;
}
.list-playlist tr td { /* Normal rows */
    background-color: #3F3F3F !important;
}
.list-playlist tr:hover td {
    background-color: #555 !important;
}
.list-playlist tbody tr:hover:not(.disc-row) .list-row-controls button, .list-playlist tbody tr:hover:not(.disc-row) .list-row-controls button:hover { /* Hover over line - background color for option / time */
    background-color: #555 !important;
}
.list-playlist tr.playing td { 
    background-color: #3C3C3C !important;
}
.list-playlist tr.playing:hover td {
    background-color: #555 !important;
}
.list-playlist tr.selected td {
    background-color: #595959 !important;
}
.list-playlist tr.selected .list-row-controls button, .list-playlist:hover tr.selected .list-row-controls button, .list-playlist tbody tr.selected:hover .list-row-controls button, .list-playlist tbody tr.selected:hover .list-row-controls button:hover { /* List options & time hover */
    background-color: #595959 !important;
}
.list-playlist tr.added .btn.btn-icon.list-track-plus:hover:not(.just-changed):not(.stay-added):before { /* Remove icon "x" in red */
    color: #D6000E !important;
}
.list-playlist tr.added .btn.btn-icon.list-track-plus:before { /* Green checkmark */
    color: #84BD00 !important;
}
tbody tr .list-track-explicit-wrap .label, tbody tr.playing:hover .list-track-explicit-wrap .label, tbody tr.selection-focus:not(.disc-row) .list-track-explicit-wrap .label,tbody tr.selection-focus:hover .list-track-explicit-wrap .label { /* Explicit tag */
    color: #aaa !important;
    background-color: #393939 !important;
    border: 1px solid #292929 !important;
}

/* SEARCH BAR */

.input-wrapper { /* Top part */
    background: #303030 !important;
}
.s-content { /* Middle part with actual content */
    background: #373737 !important;
}
.history .list-item.selected { /* Last searches list - selected item */
    box-shadow: 0px 0 0px #84BD00,  0px 0 1px #84BD00 !important;
    background: #4D4D4D !important;
}
.title { /* Headline */
    color: #ccc !important;
}
.list-group-item.list-group-item-media.selected { /* Selected item when/after typing */
    box-shadow: 0px 0 0px #84BD00,  0px 0 1px #84BD00 !important; 
}
.list-group-item.list-group-item-media.more { /* View all search */
    background: #454545 !important;
}
.list-group-item.list-group-item-media.more:hover {
    background: #4D4D4D !important;
}
.list-group-item.list-group-item-media .item-data { /* 1 Search result bar */
    background: #454545 !important;
    color: #eee !important;
}
.list-group-item.list-group-item-media .item-data:hover {
    background: #4D4D4D !important;
}
.list-group-item.list-group-item-media a .item-data:hover { /* 1. Line text hovered */
    color: #fff !important;
}
.list-item .text-secondary, .list-item .text-secondary strong { /* 2. Line */
    color: #ccc !important;
}


/* SEARCH RESULTS */

.container .s-query { /* Padding header */
    padding-left: 10px !important;
}
.container .s-header{ /* Padding search pics */
    margin-left: 20px !important;
}
  

/* ARTIST PAGE */

#artist-page .container {
    padding-right: 20px !important;
    padding-left: 20px !important;
}
.section-divider>*:first-child a, .section-divider>*:first-child>a { /* Related Artists headline */
    color: #ccc !important;
}
.section-divider>*:first-child a:hover, .section-divider>*:first-child>a:hover {
    color: #fff !important;
}
.btn-info { /* More button under top hits */
    background: #303030 !important;
}
.btn-info:hover { /* More button under top hits */
    background: #393939 !important;
    border: 1px solid #171717 !important;
}
.discography .header { /* Album headers on artist page */
    background: #444 !important;
}
.header-data .header-label { /* Year over album name */
    color: #aaa !important;
}
#related .mo-info, #related .media-info { /* Related artist boxes height */
    height: 60px !important;
    text-align: center !important;
    background: #303030 !important;
}
/* Biography text */
.biography-text .lead{
    color: #ccc !important;
    font-size: 1.2em !important;
}
.biography-text {
    max-width: 70% !important;
}
.biography-gallery {
    width: 80% !important;
    max-width: 80% !important;
}
.biography-text p {
    color: #ccc !important;
    font-size: 1.1em !important;
}    
.biography-text a {
    color: #fff !important;
    font-size: 1.1em !important;
}
.pop-meter-overlay:after { /* white bars popularity */
box-shadow: 4px 0 0 0 #aaa, 8px 0 0 0 #aaa, 12px 0 0 0 #aaa, 16px 0 0 0 #aaa, 20px 0 0 0 #aaa, 24px 0 0 0 #aaa, 28px 0 0 0 #aaa, 32px 0 0 0 #aaa;
}
.pop-meter-background:after { /* Popularity bars - not popular */
    box-shadow: 4px 0px 0px 0px #555, 8px 0px 0px 0px #555, 12px 0px 0px 0px #555, 16px 0px 0px 0px #555, 20px 0px 0px 0px #555, 24px 0px 0px 0px #555, 28px 0px 0px 0px #555, 32px 0px 0px 0px #555;
}
/* Songkick Concert Thing */
.tours .tour-caption { /* On artist page: 1. line */
    color: #bbb !important;
}
.tours .tour-location { /* 2. line */
    color: #bbb !important;
}
/* Songkick site all kind of font */
article .inner-container, article .inner-container a, article .venue, article .more-events ul .artist-name, article .headline .artist-name {
    color: #ccc !important;
}
      
      
  
/* ALBUM PAGE */
#album-page .container {
    padding-right: 20px !important;
    padding-left: 20px !important;
}

#album-page .header { /* Album page - header */
    background: #3D3D3D !important;
}
#album-page .header-bar { /* Under header - album info bar */
    background: #333 !important;
    color: #bbb !important;
}
#album-page .text-muted  {
    color: #999 !important;
}
#album-page .mo-info { /* Only for Chrome - other album info boxes at the bottom */ 
    background: #303030 !important;
    text-align: center !important;
    font-size: 1.1em !important;
    border-bottom-right-radius: 5px !important;
    border-bottom-left-radius: 5px !important;    
}
#album-page .mo-info:hover { /* Only for Chrome - other album info boxes at the bottom */ 
    background: #393939 !important;
    text-align: center !important;
    color: #fff !important;
    font-size: 1.1em !important;
    border-bottom-right-radius: 5px !important;
    border-bottom-left-radius: 5px !important;    
}
#album-page .mo-meta { /* Year */
    padding-left: 83px !important;
    color: #aaa !important;
}


/* BROWSE */

.media-info .media-description { /* Browse-Home: Descriptions in info boxes */
    color: #bbb !important;
}
#topLists .background {
    opacity: 0.3 !important;
    background-color: #000 !important;
}
#newReleases .background {
    opacity: 0.8 !important;
    background-color: #2C2C2C !important;
}
.article .leadin, .article-text p { /* Browse-News: Text */
    color: #ccc !important;
}
.article .relatedArticle {
    background: #303030 !important;
    border-top-right-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
}


/* DISCOVER */

.story .story-header { /* Font-color in reason */
    color: #bbb !important;
}
.story .story-item .story-content, .story .story-item { /* Top area and voting area */
    background: #292929 !important;
    border-radius: 5px !important;
}
.story .reason {
    text-align: center !important; 
}
    
    
/* RADIO */

#radio #playing-info .media-info { /* Title info - otherwise you get a non-transparent box */
    background: transparent !important;
}
    

/* YOUR MUSIC */

.tl-row.added .tl-save .btn:before { /* Check-mark */
    color: #84BD00 !important;
}   
.tl-row.added .tl-save:hover .btn:before { /* Hovering over check-mark => X */
    color: #D6000E !important;
}  
/* Playlists */
.container { /* Stretches playlist view */
    max-width: 100% !important;
    padding-right: 3px !important;
    padding-left: 3px !important;
}
/* Nav-bar left side */
.pf-nav { /* Small borders inbetween Playlist navigation left */
    background: #383838 !important;
}
.list-group-item.list-group-item-media.large .item-data {  /* Each playlist bar */
    background: #434343 !important;
}
.list-group-item.list-group-item-media.large:hover .item-data {
    background: #505050 !important;
}
.list-group-item.list-group-item-media.large.active .item-data {
    background: #3D3D3D !important;
}
.list-group-item.list-group-item-media.large.active:hover .item-data {
    background: #414141 !important;
}   
.pf-nav>.list-group .list-group-item .playlist-name-input-wrapper .form-group { /* Creating a new playlist */
    background: #373737 !important;
}
/*Actual playlist */    
.pf-playlist-view .header { /* Header playlists */
    background: #444 !important;
}
.header.playlist .description { /* Playlist description */
    color: #bbb !important;
}
.pf-playlist-view .header-bar { /* Bar under playlist header */
    background: #333 !important;
    color: #bbb !important;
}
.header .aside { /* Followers of playlist */
    color: #aaa !important;
}

.dropdown-menu a { /* Sorting menu in album/artist list */
    background: #393939 !important;
    color: #ccc !important;
}
.dropdown-menu a:hover {
    background: #474747 !important;
    color: #fff !important;
}  
.dropdown-menu>li {
    border: 1px solid #303030 !important;
}  

/* When clicking on an album/artist in Your Music */
header.album.header-small, header.artist.header-small {
    background: #3D3D3D !important;
}
.collection-artist .header-small.artist .header-bar, .collection-album .header-small.album .header-bar { /* bar under headline */
    background: #333 !important;
    color: #bbb !important;
}
.collection-album .text-muted { /* Album disclaimer at bottom */
    color: #999 !important;
}


/* FOLLOW */

.follow.container {
    padding-right: 20px !important;
    padding-left: 20px !important;
    max-width: 1330px !important;
}
.follow .nav { /* Otherwise text in header bar starts at the border */
    padding-left: 10px !important;
}
.follow .alert-info { /* if no facebook activated message */
    background: #10780C !important;
    color: #ddd !important;
}
.follow .remove {
    color: #777 !important;
}
.follow .remove:hover {
    color: #eee !important;
}


/* PROFILE */

.profile .container {
    padding-right: 20px !important;
    padding-left: 20px !important;
    max-width: 1330px !important;
}
.profile .header {
    background: #3D3D3D !important;
}
.profile .panel {  /* If someone doesn't follow anyone - message bar */
    background: #303030 !important;
    color: #bbb !important;
    border-radius: 5px !important;
}
.profile .one-line a { /* Names of followers in list */
    color: #ddd !important;
}
.profile .one-line a:hover {
    color: #fff !important;
}


/* MESSAGES */

#thread-body { /* Right message side */
    background: #404040 !important;
}
#thread-body .nav a { /* Name */
    color: #ddd !important;
}
#thread-body .divider .divider-title { /* Date */
    background: #333 !important;
    border: 1px solid #111 !important;
    border-radius: 5px !important;
}
.msg-grp-body { /* Text in convos */
    color: #bbb !important;
}
.msg-grp-message .time {
    color: #aaa !important;
}
.playstate-text .text-muted { /* Message if song has been played already */
    color: #999 !important;
}
#thread-not-selected h4, #no-threads h4 { /* Default view when nothing is selected */
    color: #aaa !important;
}
#thread-list .list-group-item.list-group-item-media { /* Nav-list to avoid thick borders */
    background: #444 !important;
}


/* NOTIFICATIONS */

#notification-center #title-bar {
    background: #303030 !important;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 5px !important;
}
.share-object.list-group-item {
    background-color: #393939 !important;
    color: #aaa !important;
}
.share-object.list-group-item:hover {
    background-color: #474747 !important;
    color: #aaa !important;
}


/* SETTINGS */

#section-settings .root {  /* Background */
    background: #444 !important;
    color: #ccc !important;
}
#section-settings .root a{  /* Background */
    color: #eee !important;
}
#section-settings #lang-select select { /* Language settings box */
    background-color: #444 !important;
}
#section-settings #lang-select:hover select { /* Language settings box */
    background-color: #444 !important;
    color: #fff !important;
}
#page.text-secondary, #page.text-secondary a { /* About page */
    color: #ccc !important;
}
#page .text-muted { /* "We love music" */
    color: #4aa400 !important;
}
}
