@-moz-document domain("narutoforums.com") {
    
    /** Removes the "Your Ad Here Links - Stolen From Golden Circle */  

* {

 border: none !important;

 }

div#pagenav_menu.vbmenu_popup + div {

 display: none !important;

 }

body {

 background: none repeat scroll 0 0 #272727 !important;
 color: #3b3b3b !important;
 margin: 5px 10px 10px !important;
 padding: 0 !important;

}

.page {

 background: #f0f0f0 !important;

 background-attachment: scroll !important;

 background-repeat: repeat !important;

 background-position: 0% 0% !important;

 background-size: auto !important;

 background-origin: padding-box !important;

 min-width: 950px;

 max-width: 100%;

 min-height: 20%;

 max-height: 80%;

 resize: both;

 width: 950px;

 margin-left: auto;

 margin-right: auto;

}

a:link, body_alink {

 color: #5b5b5b !important;

}

a:visited, body_avisited {

 color: #5b5b5b !important;

}

a:hover, a:active, body_ahover {

 color: #bc4c27 !important;

}

/** Sections, threads borders */
.tborder {

 background: none repeat scroll 0 0 #454545 !important;

 color: #000000 !important;

}

    /**Forum names */
    
.tcat {

 background: none repeat scroll 0 0 #535353 !important;   
 background: url('http://i.imgur.com/re7yDR7.gif') !important;
 color: #515050 !important;

 }

.tcat a:link, .tcat_alink {

   color: #515050 !important;
   text-decoration: none !important;
}
/** Forum header */

.thead {
background: none repeat scroll 0 0 #888888 !important;
 background: url('http://i.imgur.com/Vkaoct2.png') !important;
 color: #444343 !important;

}

.thead a:link, .thead_alink {

}

.thead a:visited, .thead_avisited {

 color: #444343 !important;

}

.thead a:hover, .thead a:active, .thead_ahover {

 color: #1a629f !important;

    /** Forum leaders */
}

.tfoot {

 background: none repeat scroll 0 0 #000000 !important;

 color: #000000 !important;

}

.tfoot {

 background: none repeat scroll 0 0 #e9dbd0 !important;

 color: #000000 !important;

}

.tfoot a:link, .tfoot_alink {

 color: #5e5e5e !important;

}

.tfoot a:visited, .tfoot_avisited {

 color: #5e5e5e !important;

}

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {

 color: #454545 !important;

}

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {

 color: #454545 !important;
}
    
    /** End forum leaders */
    
    /** Forum block backgrounds */
    

.alt1, .alt1Active {
    background: none repeat scroll 0 0 #f2ebe4 !important;
    border: none !important;
    color: #474646 !important;
}

.alt2, .alt2Active {

    background: none repeat scroll 0 0 #eee0d6 !important;
    border: none !important;
    color: #474646 !important;

}
    /** End Forum Block Background */
    

.wysiwyg {
 color: #ffffff !important;
 padding: 0 !important;
}

.wysiwyg a:link, .wysiwyg_alink {
 color: #1e1e1e !important;
}

.wysiwyg a:visited, .wysiwyg_avisited {
 color: #1e1e1e !important;
}

.wysiwyg a:hover, .wysiwyg a:active, .wysiwyg_ahover {
 color: #1e1e1e !important;
}

.time {

 color: #76563e !important;

}

.highlight {

 color: #FF0000 !important;

}

legend {

 color: #1e1e1e !important;
}
   
    .panel {
    background: none repeat scroll 0 0 #ddcec2 !important;
    color: #2d2b2b !important;
    padding: 10px !important;
    
    }

.panelsurround {

    background: none repeat scroll 0 0 #f0f0f0 !important;
    color: #1e1e1e !important
        }
        

/** Top control bar */

.vbmenu_control {

 background: none repeat scroll 0 0 #d0cccc !important;
 color: #5b5b5b !important;
 padding: 3px 6px !important;
 white-space: nowrap !important;

}

.vbmenu_control a:link, .vbmenu_control_alink {
 color: #6d6d6d !important;
}

.vbmenu_control a:visited, .vbmenu_control_avisited {
 color: #6d6d6d !important;
}

.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover {
 color: #5b5959 !important;
}

.vbmenu_popup {
 background: none repeat scroll 0 0 #ebebeb !important;
 border: 1px solid #0B198C !important;
 color: #111314 !important;
}

.vbmenu_option {
 background: none repeat scroll 0 0 #ebebeb !important;
 color: #111314 !important;
 cursor: pointer !important;
 white-space: nowrap !important;

}

.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
 color: #005a6e !important;
 text-decoration: none !important;
}

.vbmenu_hilite {
 background: none repeat scroll 0 0 #454545 !important;
 color: #c5bbba !important;
 cursor: pointer !important;
 white-space: nowrap !important;
}

.vbmenu_hilite a:link, .vbmenu_hilite_alink {
 color: #c5bbba !important;
 text-decoration: none !important;
}

.vbmenu_hilite a:visited, .vbmenu_hilite_avisited {
 color: #c5bbba !important;
 text-decoration: none !important;
}

.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
 color: #c5bbba !important;
 text-decoration: none !important;
}


.vbmenu_popup {
 background: none repeat scroll 0 0 #d9e5ef !important;
 color: #000000 !important;
 border: 1px solid #d5cece !important;
}

.vbmenu_option a:link, .vbmenu_option_alink {
 color: #1e1e1e !important;
 text-decoration: none !important;
}

.vbmenu_option a:visited, .vbmenu_option_avisited {
 color: #1e1e1e !important;
 text-decoration: none !important;
}

.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
 color: #ffffff !important;
 text-decoration: none !important;
}

td.thead, th.thead, div.thead {
 padding: 4px !important;
}

.pagenav td {
 padding: 2px 4px !important;

}

.shade, a.shade:link, a.shade:visited {
 color: #777777 !important;
}

a.shade:active, a.shade:hover {
 color: #FF4400 !important;
 text-decoration: underline !important;
}

.tcat .shade, .thead .shade, .tfoot .shade {
 color: #DDDDDD !important;
}

.fieldset {
 margin-bottom: 6px !important;
}

center tr:nth-child(1) td {
 display: none !important;
 }

.bigusername a:link, .bigusername_alink {
 color: #ffffff !important;

}

.bigusername a:visited, .bigusername_avisited {
