css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wunderground.com"), domain("m.wund.com"), domain("i.wund.com") {
    
/*  header */
    
    NAV.topbar #global-header .logo {
    position: absolute;
    top: 0px;
    left: 40px;
    width: 171px;
    height: 38px;
    background-image: url('http://oi57.tinypic.com/4ui1c1.jpg');
    background-position: left;
    background-repeat: no-repeat;
}
    
.block-list .block-item:nth-child(2n+1) {
    background-color: #78AB46;
}

#tropicNotice IMG, /* footer*/ #footer, 
#hpSearch #title, #tripIcons, .adBox.br5,
#socialWeather, #pwsTable > TFOOT tr td,
/* social BS >*/.titlePromo, #optShare, .sep, #plusOne, #likeButton, #socialBar > TBODY > TR > TD, #postShare, #locationBarBottom > TBODY > TR > TD:nth-child(6)
{ display: none !important; }

.readTerms, #locationName, .primeHeader .setAlerts,
.photoUp, .photoBy, .cmSource.cmFWIX, #fct_details .fct_hourlytable TABLE
{ background-image: none !important;}
    
/* font */
a, .keyText
{color: #215E21 !important;}
    
a:hover, #optionBar a:hover, #trip_panel_0 select option:hover, SPAN[style="color: rgb(0, 170, 0);"]
{ color: #83F52C !important;}

#optionBar a, #grouptitle, div, .rfGreen
{ color: black !important;}

h1
{ font-size: 16px !important;}

.green
{ color: #FCD116 !important;
text-shadow: 1px 2px 1px #000 !important;}
        
.admin-label 
{ color: #FCB514 !important;
text-shadow: 1px 2px 1px #000 !important;}    
.comment.admin {
background-color: #FF8C00;}
             
SPAN.subtle {
color: #660000;}

/*  header */

#pageHeader
{ height: 120px !important; }
    
#feature-menu .primary-menu LI:hover > LABEL.maps, #feature-menu .primary-menu LI.active > LABEL.maps {
border-top-color: #D5202A;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.maps {
border-left-color: #D5202A;
}
    
#feature-menu .primary-menu LI:hover > LABEL.severe, #feature-menu .primary-menu LI.active > LABEL.severe {
border-top-color: #FFA500;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.severe {
border-left-color: #FFA500;
}
    
#feature-menu .primary-menu LI:hover > LABEL.news, #feature-menu .primary-menu LI.active > LABEL.news {
border-top-color: #FFFF00;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.news {
border-left-color: #FFFF00;
}
    
#feature-menu .primary-menu LI:hover > LABEL.photos, #feature-menu .primary-menu LI.active > LABEL.photos {
border-top-color: #228B22;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.photos {
border-left-color: #228B22;
}
    
#feature-menu .primary-menu LI:hover > LABEL.activities, #feature-menu .primary-menu LI.active > LABEL.activities {
border-top-color: #191970;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.activities {
border-left-color: #191970;
}
    
#feature-menu .primary-menu LI:hover > LABEL.more, #feature-menu .primary-menu LI.active > LABEL.more {
border-top-color: #4B0082;
}
    
.sidebar .sidebar-wrapper .side-main-nav A.browse {
border-left-color: #4B0082;
}
    
.maps-radar .subnav LI A:link, .maps-radar .subnav LI A:visited, .maps-radar .subnav LI LABEL:link, .maps-radar .subnav LI LABEL:visited {
border-color: #D5202A;
}
    
.maps-radar .subnav LI A.selected, .maps-radar .subnav LI A.selected:hover, .maps-radar .subnav LI LABEL.selected, .maps-radar .subnav LI LABEL.selected:hover {
color: #1E2023;
border-color: #FF6347;
}
    
.news-blogs .subnav LI A:link, .news-blogs .subnav LI A:visited, .news-blogs .subnav LI LABEL:link, .news-blogs .subnav LI LABEL:visited {
border-color: #FFFF00;
}
    
.news-blogs .subnav LI A.selected, .news-blogs .subnav LI A.selected:hover, .news-blogs .subnav LI LABEL.selected, .news-blogs .subnav LI LABEL.selected:hover {
color: #1E2023;
border-color: #FFFFFF;
}
    
.severe-weather .subnav LI A:link, .severe-weather .subnav LI A:visited, .severe-weather .subnav LI LABEL:link, .severe-weather .subnav LI LABEL:visited {
border-color: #FFA500;
}
    
.severe-weather .subnav LI A.selected, .severe-weather .subnav LI A.selected:hover, .severe-weather .subnav LI LABEL.selected, .severe-weather .subnav LI LABEL.selected:hover {
color: #1E2023;
border-color: #FFD700;
}
    
.photos-videos .subnav LI A:link, .photos-videos .subnav LI A:visited, .photos-videos .subnav LI LABEL:link, .photos-videos .subnav LI LABEL:visited, .webcams .subnav LI A:link, .webcams .subnav LI A:visited, .webcams .subnav LI LABEL:link, .webcams .subnav LI LABEL:visited {
border-color: #228B22;
}
    
.photos-videos .subnav LI A.selected, .photos-videos .subnav LI A.selected:hover, .photos-videos .subnav LI LABEL.selected, .photos-videos .subnav LI LABEL.selected:hover, .webcams .subnav LI A.selected, .webcams .subnav LI A.selected:hover, .webcams .subnav LI LABEL.selected, .webcams .subnav LI LABEL.selected:hover {
color: #1E2023;
border-color: #7CFC00;
}
    
/* ========================= */
html, body
{background: #78AB46 linear-gradient(to top,rgba(205,193,197, 0.1) 5%, rgba(205,193,197, 0.3) 75%, rgba(205,193,197, 0.6)) fixed !important; }

#locationName
{ margin-left: -20px !important; }

HTML > BODY > CENTER:nth-child(2)
{ margin-top: 20px !important; }
  
#feature-menu .menu-wrapper.active
.menu-list LI A:hover {
color: #A9A9A9;
background-color: #4C4C4C;
border-left-color: #228B22;}
   
    
/* error popup */
.wui-log-body, .wui-log-time, .wui-log-close {color: red !important; }
    
/* search autocomplete popup */
    
.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all,
.ui-autocomplete-group
{background: rgb(169, 169, 169) !important;
border: 1px solid black !important;
color: grey !important; }

/* other pages */
/* comments */
.pic, .text, .subG,
#PostComment, #PostComment > DIV
{background: rgba(160,36,34, .2) !important;
border: 4px solid rgba(0,0,0, .2) !important; }
    
.comment.gone {
background-color: #DC143C; }
    
.comment.owner {
background-color: #BDB76B; }
    
BLOCKQUOTE {
        background: none repeat scroll 0% 0% #99C68E;}

#blog-entry p {
    line-height: 1.5;
    background: rgba(108,196,23, .2) !important;
    border: 4px solid rgba(0,0,0, .2) !important; }
        
#favorites .favorites-bar {
    height: 39px;
    background: rgba(204,50,50, .2) !important;
    border: 4px solid rgba(0,0,0, .2) !important; }

FOOTER.primary .navigation LI {
    line-height: normal;
    margin-bottom: 10px;
    background: rgba(59,83,35, .2) !important;
    border: 4px solid rgba(0,0,0, .2) !important;}
    
.wx-content .wx-col, .wx-content .wx-col-1, .wx-content .wx-col-2, .wx-content .wx-col-3 {
    padding-left: 0.625rem;
    padding-right: 0.625rem;
    float: none;
    width: auto;
    background: rgba(59,83,35, .2) !important;
    border: 4px solid rgba(0,0,0, .2) !important;}
    
.column, .columns {
    position: relative;
    padding-left: 0.625rem;
    padding-right: 0.625rem;
    float: left;
    background: rgba(59,83,35, .2) !important;
    border: 4px solid rgba(0,0,0, .2) !important;}
   
    #feature-menu .menu-wrapper.active .menu-list {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border: 1px solid #1E2023;
    background-color: #78AB46;}
    
FOOTER.primary .navigation {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #3B5323;}
    
    FOOTER.primary .company {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #3B5323;}
    
    FOOTER.primary .floor {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #3B5323;}
    
    BODY .button, BODY button {
       background-image: linear-gradient(#088A29, #088A29);
       background-color: #088A29;}
    
[type="button"]:hover, BODY [type="submit"]:hover, BODY 
.button:hover, BODY button:hover {
    background-color: #202020;
    background-image: linear-gradient(#202020, #202020);}
    
.plus-button .plus {
    border-radius: 0px 5px 5px 0px;
    background-color: #088A29;
    background-image: linear-gradient(#088A29, #088A29);}

.plus-button:hover 
.plus:hover {
    background-color: #202020;
    background-image: linear-gradient(#202020, #202020);
    border: 1px solid #F83;}
    
/* Profile Menu */

#wuSettings-anon P:first-child {
cursor: default;
background-color: #FF6347;
color: #202020;
font-weight: bold;
text-align: center;
height: 44px;
line-height: 44px;
margin: 0;}

#wuSettings-anon FORM LABEL {
font-size: 0.75rem;
background-color: #78AB46;
color: #4C4C4C;}

#wuSettings-anon FORM A {
height: 34px;
line-height: 34px;
background-color: #78AB46;
font-size: 0.75rem;
display: inline-block;
float: right;
}
#facebook-sign-in {
border-top: 1px solid #d7d7d7;
background-color: #78AB46;
position: relative;
padding: 25px 0;
text-align: center;}

#wuSettings-anon FORM {
width: 100%;
background-color: #78AB46;
padding: 0 10px;
margin-bottom: 20px;}

#wuSettings-user LI:first-child {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    cursor: default;
    background-color: #FF6347;
    color: #202020;
    font-weight: bold;
    text-align: center;
    height: 35px;
    line-height: 35px;
    border-bottom: medium none;}

#wuSettings-user LI A {
    text-decoration: none;
    background-color: #78AB46;
    color: #4C4C4C;}
    
#wuSettings-user LI.no-link {
    line-height: 30px;
    height: 30px;
    background-color: #D5202A;
    font-weight: bold;
    text-align: center;
    color: #202020;
    cursor: default;
}
    
/* Other Settings */
    
#wuSettings-quick .button-group {
    margin: 13px 10px;
    background-color: #78AB46;}
    
    #wuSettings-more {
    display: block;
    height: 40px;
    background-color: #D5202A;
    line-height: 40px;
    padding-left: 10px;
    font-size: 0.9375rem;
    border-top: 1px solid #D7D7D7;
    text-decoration: none;
    color: #202020;}
}
