css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitch.tv"){
    
/* Darken background */
#main_col, .directory-main, .app-main, .js-loading {background-color:#191919 !important;}
.directory_header_contain {background:rgba(25, 25, 25, 0.93) !important;}
#custom_bg.tiled, #custom-bg.tiled {padding-top:9999px !important;}
#channel {margin-top:-30px !important; padding-top:30px !important;}

/* Chat Background */
.ember-chat .chat-room{
background-color:#2a2a2a !important;}
  
/* Not Log-in Background */
.no-login-contain {
padding: 20px;
background-color:#666666 !important;}
.button-simple.primary {
background: none repeat scroll 0% 0% #999;}

/* Badges */
.ember-chat .badges .badge {
  background-position:center!important;
  min-width: 20px!important;
  height: 20px!important;
  -webkit-border-radius: 30px!important;}

.ember-chat .badges .broadcaster { background-image: url("http://oi58.tinypic.com/21o2f5k.jpg"), linear-gradient(to bottom right, #C11B17, #1d3949)!important;}
.ember-chat .badges .moderator {background-image: url("http://i.adultswim.com/adultswim/tools/img/home/asmb/iconz/moderator.gif"), linear-gradient(#5E5A80, #575200)!important;}
.ember-chat .badges .turbo {background-image: url("http://i.imgur.com/hN5chbu.png"), linear-gradient(to bottom right, #5E5A80, #575200)!important;}
.ember-chat .badges .subscriber {background-image: url("http://www.twitch.tv/favicon.ico"), linear-gradient(to bottom right, #660000, #575200)!important;}
.ember-chat .badges .ffz-donor {background-image: url("http://i.imgur.com/DF01wuj.png"), linear-gradient(to bottom right, #FFF000, #575200)!important;
background-repeat: no-repeat!important;} 

/*Text*/
 
/* Set default name and chat colour */

#chat_lines li p[style*="color"], #chat_lines li a.nick, .chat-line .from { color: #999 !important; }
#chat_lines li .chat_line, .message { color: #FFFFEE !important; }
.chat-line, #viewer_list 
{color:#000000!important;
border-bottom: 1px solid rgb(0, 0, 0) !important;
padding-left:5px !important;
padding-right:5px !important;
margin-right:0px !important;
padding-top:3px !important;
padding-bottom:3px !important;
line-height:15px !important;
box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset !important;
font-size:14px;}    
    
#channel .player-column #broadcast-meta .info .title {
    font-size: 26px;
    color: #666666;
    line-height: 35px;
}
    
/* Chat No Emotes In Chat*/
[class*="emo"]{display:none!important;}
[class*="turbo"]{display:none!important;}
[class*="subscriber"]{display:none!important;} 
  
/* Darken input boxes */
    
.button.primary {
background: #666666;
color: #fff;
padding: 0 10px;
font-size: 12px;}
    
.button {
    display: inline-block;
    position: relative;
    cursor: pointer;
    line-height: 30px;
    height: 30px;
    font-size: 14px;
    font-family: inherit;
    padding: 0px;
    color: #666666;
    border: 0px none;
    background: none repeat scroll 0% 0% transparent;}
    
.ember-chat .chat-interface .textarea-contain textarea {
    width: 100% !important;
    height: 100% !important;
    background-color:#2a2a2a !important;
    padding-right: 25px;
    resize: none;
    -moz-box-sizing: border-box;}
  
/* Darken ember chat menus */
    
.top {
    position: absolute;
    top: 0px;
    margin: 0px;
    padding: 0px;
    width: 100%;
    background-color: #E66C2C;
    z-index: 1;}
    
.archives-contain .videos {
    padding: 0px 20px;
    background-color: #2a2a2a;}

.ember-chat .chat-menu .chat-menu-header {
color: #202020;
background-color: #F88017;
border-bottom: 1px solid rgba(0,0,0,0.2);
padding: 10px 0;
margin: 0 20px;
z-index: 101;
}
    
.ember-chat .chat-header {
    top: 0px;
    left: 20px;
    right: 20px;
    position: absolute;
    height: 50px;
    line-height: 50px;
    text-align: center;
    font-size: 14px;
    background-color: #E66C2C;}
a {
    color: #666666;
    font-size: 100%;
    text-decoration: none;}
    
.ember-chat .chat-menu .chat-top-header p {
    line-height: 50px;
    text-align: center;
    font-size: 14px;
    background-color: #E66C2C;}
    
.ember-chat .ember-list-view {
    overflow: auto;
    position: relative;
    width: 100%;
    background-color: #2a2a2a;}
    
.ember-chat .chat-settings {
    width: 222px;
    z-index: 1;
    position: absolute;
    background-color: #2a2a2a;
    bottom: 50px;
    left: 0px;
    padding: 10px 0px;}
  
/* Chat Font Options*/

#chat_line_list,.ember-chat .chat-messages .chat-line{font-family: "Gadget", Gadget !important;}

#large_nav .game_filter.selected a {
    background-color: rgb(21, 21, 21) !important;
    color: rgb(255, 255, 255) !important;
    border-left: 4px solid #666666;}

#small_nav .game_filter.selected a {
    border-left: 3px solid #666666;
    background-color: #151515;}

/* Inbox Messages*/

.messages div.message {
    padding: 10px;
    background-color: #202020;
    border-bottom: 1px solid #E0E0E0;
    position: relative;
    min-height: 60px;}

/* Scroll */

.tse-scrollbar .drag-handle {
position: absolute;
right: 2px;
-webkit-border-radius: 7px;
-moz-border-radius: 7px;
border-radius: 7px;
min-height: 10px;
width: 6px;
opacity: 0;
-webkit-backface-visibility: hidden;
-webkit-transition: opacity 0.2s linear;
-moz-transition: opacity 0.2s linear;
-o-transition: opacity 0.2s linear;
-ms-transition: opacity 0.2s linear;
transition: opacity 0.2s linear;
background: #666666;
-webkit-background-clip: padding-box;
-moz-background-clip: padding;
box-shadow: 0 0 1px 1px rgba(255,255,255,0.25);
-moz-box-shadow: 0 0 1px 1px rgba(255,255,255,0.25);
-webkit-box-shadow: 0 0 1px 1px rgba(255,255,255,0.25);
}

/* Chat room list */

.ember-chat .chat-room-list {
top: 0;
left: 0;
right: 0;
bottom: 0;
position: absolute;
background: #F88017;
z-index: 0;
padding-top: 50px;
overflow: hidden;}
    
}
