css:
/*======================================
  Ashisuto oss ja jp liquid compact 2014/05
  prefix("http://www.ashisuto.co.jp/product/category/oss/")
  ======================================*/

@-moz-document url-prefix("http://www.ashisuto.co.jp/product/category/oss/") {
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}
body{
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }


/*--- header ---*/
#header {position: static !important; width: auto !important; height: auto !important;}
#header #site_name {float: left; position: static !important; padding-right: 0.5em;}
#header #header_inquiry {float: right; position: static !important;}
#header #tagline, #header #nav_helper, #header #header_search
 {float: left; position: static !important;}
#header #nav_global {clear: both; position: static !important;}

/*--- footer ---*/
#footer #footer_inner {width: auto !important; padding: 0 !important;}
#footer #nav_footer dl {width: auto !important;}
#footer #footer_iso_badge {clear: both; width: auto !important;}
#footer #footer_iso_badge .img {float: left;}
#footer #footer_mail {clear: both; width: auto !important;}
#footer #copyright_holder {padding: 0 !important;}

/*--- main contents ---*/
/*-- navigation path --*/
#topic_path {clear: both;}
#topic_path p {width: auto !important; padding: 0 !important;}

/*-- 2columns layout --*/
#content {width: auto !important; margin: 0 !important;}
#content_main {width: 100% !important; margin: 0 -150pt 0 0 !important;}
#content_main>* {width: auto !important; margin: 0 150pt 0 0 !important;}
#content_sub {width: 150pt !important;}

/*-- in left (main) --*/
.hd02, .jin .headLv2-A h2,
.hd04, .jin .headLv4-A h4
 {margin: 0 !important; padding: 0 !important;}
#content_main .section .jin {margin: 0 !important;}
#content_main .section .inquiry {
 background-size: 100% auto !important;
 background-position: 0 -1em !important;
 width: 100% !important;
 margin: 0 !important; padding: 0 !important;
 }
#content_main .section .inquiry h3{width: auto !important;}
#content_main .section .inquiry .outline {width: auto !important;}
#content_main .relations .item {padding: 0 !important;}
#content_main .relations h3 {margin: 0 !important;}

/*-- in right (sub) --*/

}
