css:
@-moz-document domain("github.com")
{
  body, input, select, textarea, button
  {
    font-family: "Segoe UI", sans-serif;
  }
  
  body { color: #000 !important; }
  a { color: #00f; }
  
  .header
  {
    background: rgb(214, 219, 233) !important;
    text-shadow: none !important;
  }
  
  .pagehead
  {
    background-color: rgb(41, 57, 85) !important;
  }
  .pagehead h1 { color: #fff; text-shadow: none; }
  .pagehead.repohead h1 { color: #999 !important; }
  .pagehead a { color: #ccf !important; }
  
  .notification-indicator
  {
    border-left: 1px solid transparent;
    border-right: 1px solid transparent;
  }
  
  .box-header
  {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
  }
  
  .box-body
  {
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
  }
  
  .pagehead .button, .pagehead .minibutton
  {
    color: #fff !important;
    background: rgb(54, 78, 111) !important;
    border: 1px solid rgb(54, 78, 111) !important;
    border-radius: 0 !important;
    text-shadow: none;
  }
  .pagehead .button:hover, .pagehead .minibutton:hover
  {
    background: rgb(91, 113, 153) !important;
    border: 1px solid rgb(91, 113, 153) !important;
  }
  .pagehead .button:focus, .pagehead .minibutton:focus,
  .pagehead .minibutton.selected
  {
    color: #000 !important;
    background: rgb(255, 242, 157) !important;
    border: 1px solid rgb(255, 242, 157) !important;
    box-shadow: none;
  }
  
  .pagehead .social-count
  {
    color: #fff !important;
    background: rgb(54, 78, 111) !important;
    border: 1px solid rgb(54, 78, 111) !important;
    border-top-right-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
  }
  .pagehead .social-count:hover
  {
    background: rgb(91, 113, 153) !important;
    border: 1px solid rgb(91, 113, 153) !important;
  }
  .pagehead .social-count:active
  {
    color: #000 !important;
    background: rgb(255, 242, 157) !important;
    border: 1px solid rgb(255, 242, 157) !important;
  }
  
  .pagehead .octicon-eye-watch:before { color: #f63 !important; }
  .pagehead .octicon-eye-unwatch:before { color: #c30 !important; }
  .pagehead .octicon-star:before { color: #ff0 !important; }
  .pagehead .octicon-star-delete:before { color: #ffc !important; }
  .pagehead .octicon-git-branch-create:before { color: #0cc !important; }
  
  .markdown-body h1, .markdown-body h2, .markdown-body h3
  {
    font-weight: 600;
  }
  
  input[type="text"], #adv_code_search .search-page-label, input[type="password"], input[type="email"], input[type="number"], input[type="tel"], input[type="url"], textarea
  {
    border-radius: 0 !important;
  }
  
