css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('mangaupdates.com') {

    /*------------------------------------------------------------get rid of banners----------*/
    td {
        background-image: initial !important;
    }

    div {
        background-image: initial !important;
    }
    
    img {
        display: none !important;
    }

    #banner {
        height: 5px !important;
    }

    #maintitle {
        top: 0 !important;
    }
    
    #signupbutton {
        display: none !important;
    }
    
    #login_box_padding {
  		position: absolute;
  		right: 125px !important;
		top: 5px !important;
	}

    /*get rid of ios app add*/
    #left_row1 div {
  		display: none !important;
    }

	#left_row1 {
  		height: 1px !important;
  		background-color: #444 !important;
  		overflow: hidden !important;
    }
    
    /*------------------------------------------------------------get rid of img----------*/
    .tab_left {
        display: none !important;
    }
    
    .tab_right {
        display: none !important;
    }
    
    #main_content .releasestitle img[src="images/review_img.jpg"] {
        display: none !important;
    }
    
     #main_content #minfotable img[src="images/mcard.jpg"] {
        display: none !important;
    }
    
    img[src^="images/poll"] {
        height: 0 !important;
        padding-top: 8px !important;
        background-color: #F1F1F1 !important;
    }
    
     /*display image in poll*/
    #left_row2 img {
        display: initial !important;
    }
    
    /*display image in main*/
    #main_content img {
        display: initial !important;
    }
    
    /*------------------------------------------------------------get rid of unneeded box at top and bottom----------*/
    #center_row1 {
        height: 1px !important;
  		background-color: #F1F1F1 !important;
        border: initial !important;
    }
    
    #center_row1_content {
        height: 0 !important;
    }
    
    .content tr:last-child td td.center_content {
        background-color: #444 !important;
    }
    
    /*------------------------------------------------------------get rid of borders----------*/
    hr {
       opacity: 0 !important;
    }
    
    th {
        border: initial !important;
    }
    
    .left_content {
        border: initial !important;
    }
    
    #left_row2 {
        border: initial !important;
    }
    
    .right_content {
        border: initial !important;
    }
    
    #right_row1 {
        border: initial !important;
    }
    
    #right_row2 {
        border: initial !important;
    }
    
    .news_container {
        border: initial !important;
    }
    
    .news_title {
        border: initial !important;
    }
    
    .news_content {
        border: initial !important;
    }
    
    .table_content {
        border: initial !important;
    }
    
    .frow {
        border: initial !important;
    }
    
    .alt {
        border: initial !important;
    }
    
    .comment_rating {
        border: initial !important;
    }
    
    .spoiler {
        border: initial !important;
    }
    
    #minfoleft {
        border: initial !important;
    }
    
    .minfo_bar {
        border: initial !important;
    }
    
    .releasestitle td {
        background-color: #F1F1F1 !important;
    }
    
    div[style="border: 1px solid #7F8E9E; padding: 2px;"] {
        padding: 0px !important;
    }
    
    .lcol4 {
        border: initial !important;
    }
    
    .lcol4_single {
        border: initial !important;
    }
    
    .lcol4_top {
        border: initial !important;
    }
    
    .lcol4_bottom {
        border: initial !important;
    }
    
    .lcol1 {
        border: initial !important;
    }
    
    .lcol1_top {
        border: initial !important;
    }
    
    .lcol1_bottom {
        border: initial !important;
    }
    
    /*insert border-spacing*/
    .content {
         border-spacing:2px 0px !important;
    }
    
    .content .content {
         border-spacing:initial !important;
    }
    
    /*------------------------------------------------------------change color theme----------*/
    html, body {
        background-color: #444 !important;
    }
    
    .center_content {
        background-color: #F1F1F1 !important;
    }
    
    .left_content {
        background-color: #444 !important;
    }
    
    #left_row2 {
        background-color: #444 !important;
    }
    
    .right_content {
        background-color: #444 !important;
    }
    
    #right_row1 {
        background-color: #444 !important;
    }
    
    .alt {
        background-color: #FFF !important;
    }
    
    .sCat {
        background-color: #FFF !important;
    }
    
    th[class^="comment"] {
        background-color: #FFF !important;
    }
    
    th[class^="comment"] div {
        background-color: #FFF !important;
    }
    
    .spoiler {
        background-color: #FFF !important;
        color: #FFF !important;
    }
    
    .spoiler.mouseover:hover {
        color: #444 !important;
    }
    
    .table_content {
        background-color: #F1F1F1 !important;
    }
    
    #minfoleft {
        background-color: #F1F1F1 !important;
    }
    
    .fheadrow {
        background-color: #FFF !important;
    }
    
    .frow1 {
        background-color: #FFF !important;
    }
    
    .frow2 {
        background-color: #FFF !important;
    }
    
    .options {
        background-color: #FFF !important;
    }
    
    .username {
        background-color: #FFF !important;
    }
    
    td[bgcolor="#F0F3F7"] {
        background-color: #F1F1F1 !important;
    }
    
    td[bgcolor="#E4E7EB"] {
        background-color: #FFF !important;
    }
    
    form[name="seriesForm"] #list_table .alt {
        background-color: #FFF !important;
    }
    
    .lcol4 {
        background-color: #FFE8E2 !important;
    }
    
    .lcol4_single {
        background-color: #FFE8E2 !important;
    }
    
    .lcol4_top {
        background-color: #FFE8E2 !important;
    }
    
    .lcol4_bottom {
        background-color: #FFE8E2 !important;
    }
    
    .lrow {
        height: 100%  !important;
    }
    
    .lcol_nopri {
        display: table !important;
        width: 395px !important;
        height: 100%  !important;
        padding-top: 0 !important;
        padding-right: 0 !important;
    }
    
    .lcol_nopri a {
        display: table-cell !important;
        width: 1px !important;
        white-space: nowrap !important;
    }
    
    .newlist {
        display: table-cell !important;
        padding: 0 0 0 50px !important;
        background-color: #FFE8E2 !important;
        height: 100%  !important;
        font-weight: 900 !important;
    }
    
    .newlist a {
        width: 100% !important;
    }
    
    .lcol1 {
        background-color: #FFE8E2 !important;
    }
    
    .lcol1_top {
        background-color: #FFE8E2 !important;
    }
    
    .lcol1_bottom {
        background-color: #FFE8E2 !important;
    }
    
    /*font color*/
    .large_bold {
        color: #F1F1F1 !important;
    }
    
    .medium_bold {
        color: #F1F1F1 !important;
    }
    
    .small_bold {
        color: #F1F1F1 !important;
    }
    
    .text {
        color: #444 !important;
    }
    
    .specialtext {
        color: #444 !important;
    }
    
    .textsmall {
        color: #444 !important;
    }
    
    .left_content .textsmall {
        color: #F1F1F1 !important;
    }
    
    .textbold {
        color: #444 !important;
    }
    
    .left_content .textbold {
        color: #F1F1F1 !important;
    }
    
    .titlebig {
        color: #444 !important;
    }
    
    .titlesmall {
        color: #444 !important;
    }
    
    .releasestitle {
        color: #444 !important;
    }
    
    .newsname {
        color: #F1F1F1 !important;
    }
    
    #main_content .newsname {
        color: #444 !important;
    }
    
    .tab_middle {
        color: #444 !important;
    }
    
    /*a*/
    a:link, a:visited {
        color: #844 !important;
    }
    
    a:hover, a:active {
        color: #800 !important;
    }
    
    .left_content a:link, .left_content a:visited {
        color: #FBB !important;
    }
    
    .left_content a:hover, .left_content a:active {
        color: #F77 !important;
    }
    
    #login_box a:link, #login_box a:visited {
        color: #FBB !important;
    }
    
    #login_box a:hover, #login_box a:active {
        color: #F77 !important;
    }
    
    .right_content a:link, .right_content a:visited, .right_content a:hover, .right_content a:active {
        color: #F1F1F1 !important;
    }
    
    /*alternating colors news page*/
    #main_content tr:nth-child(even) {
        background-color: #FFF !important;
    }
    
    #main_content tr tr:nth-child(even) {
        background-color: inherit !important;
    }
    
    .news_content {
        background-color: inherit !important;
    }
    
    .news_author {
        background-color: inherit !important;
    }
    
    .news_comments {
        background-color: inherit !important;
    }
    
    /*shadow*/
    .center_content .content {
        box-shadow: 0px 0px 5px #000 !important;
        position: relative !important;
        z-index: 10 !important;
    }
    
    #maintitle {
        top : -30px !important;
    }
    
    /*form and input*/
    .loginbox {
        border: initial !important;
        border-radius: 3px !important;
        box-shadow: 0 0 5px #666 inset !important;
        background-color: #F1F1F1 !important;
        color: #444 !important;
    }
    
    .small_input {
        border: initial !important;
        border-radius: 3px !important;
        box-shadow: 0 0 5px #666 inset !important;
        background-color: #F1F1F1 !important;
        color: #444 !important;
    }
    
    .inbox {
        border: initial !important;
        border-radius: 3px !important;
        box-shadow: 0 0 5px #666 inset !important;
        background-color: #FFF !important;
        color: #444 !important;
    }
    
    .inbox[type="submit"], .inbox[type="button"], .button {
        border:initial !important;
        border-radius: 3px !important;
        box-shadow: 0 0 1px #666 !important;
        background-color: #FFF !important;
        color: #444 !important;
    }
    
    input[type="image"] {
        border:initial !important;
        height: 0px;
        width: 29px;
        padding-top: 20px;
        background: url(data:image/gif;base64,);
    }
}
