/* BK sidebar color
======================= */
/*
.color_U029N7BTY span.overflow-ellipsis {
    color: #4b42cc!important;
}
*/
  html {
    background-color: #141414!important;
}

div.message.show_user.avatar.divider {
    color: #a3a3a3!important;
}

span.message_content {
    color: #b3b3b3!important;
}

a.msg_actions {
    background-color: transparent !important;
}
#message-input {
    background-color: #141414!important;
    color: #bdbdbd!important;
}

#message-form {
    background-color: #141414!important;
    color: #bdbdbd!important;
}

#messages-input-container {
    background-color: #141414!important;
    color: #bdbdbd!important;
}

div.row-fluid {
    background-color: #141414!important;
    color: #bdbdbd!important;
}

span.divider_label {
    background-color: transparent !important;
}

div.unread_divider.no_unreads hr {
    margin-top: 3px !important;
    margin-bottom: -5px !important;
}
div.day_divider {
    background-color: #cfcfcf !important;
    border-radius: 5px !important;
    box-shadow: inset 0px 0px 10px -1px black !important;
}

div.day_divider_label {
    background-color: #cfcfcf !important;
    background: linear-gradient(to bottom,  #45484d 0%,#000000 100%) !important;
    
    border-radius: 4px !important;
    color: #999999 !important;
}

/*Fix for body color change 11/13/2015 */

.message_content span.message_body {
    color: #bdbdbd !important;
}
ts-message.active:not(.standalone):not(.multi_delete_mode),
ts-message:hover:not(.standalone):not(.multi_delete_mode) {
    background: #171717 !important;
    border-radius: 5px !important;
}
ts-message .action_hover_container a {
    color: #B6B7B7 !important;
    background: #3A3A3A !important;
}
ts-message .action_hover_container a:hover {
    color: #3aa3e3 !important;
}
.menu #monkey_scroll_wrapper_for_menu_items_scroller {
    background: #272727 !important;
    border-radius: 6px !important;
}
.menu ul li a {
    display: block;
    background: #272727;
    color: #B5B5B5;
}
.menu ul li.highlighted a,
.menu:not(.keyboard_active) ul li:hover a {
    background: #3aa3e3 !important;
    color: #fff !important;
    text-decoration: none;
    overflow: hidden;
    text-shadow: 0 1px 0 rgba(0, 0, 0, .1);
}
#notification_bar {
    background-color: #141414 !important;
}
#primary_file_button {
    background: #171717 !important;
}
.ts_icon.ts_icon_plus_thick {
    -webkit-filter: invert(100%);
    filter: invert(100%) !important;
}
#client-ui .monkey_scroll_handle_inner {
    border: 3px solid #868686 !important;
}
.menu .section_header .header_label {
    background-color: #272727;
    color: #D8D8D8 !important;
}
#primary_file_button.active,
#primary_file_button:hover {
    background: #56B68B !important;
    color: #000 !important;
    border-color: #56B68B !important;
}

#message_edit_form .emo_menu, #messages-input-container .emo_menu, .inline_message_input_container .emo_menu{
color:#6F6F6F !important;
}

ts-message.active:not(.standalone):not(.multi_delete_mode):not(.highlight), ts-message:hover:not(.standalone):not(.multi_delete_mode):not(.highlight) {
    background: #171717 !important;
}


/* fix on /3/9/2016 for header being white */

div.channel_header {
    background-color: #121212 !important;
}

span.channel_topic.inline_edit.inline_edit_editable.inline_edit_hover {
    background-color: #121212 !important;
}

#channel_header_info {
    background-color: #121212 !important;
}

div.channel_title_info {
    background-color: #121212 !important;
}

div.flex_header {
    background-color: #121212 !important;
}

.channel_header_refresh #client_body:not(.onboarding):before {
    background: #121212;
    border-color: grey !important;
}

#messages_container.header_refresh.has_top_messages_banner:before {
    background: transparent !important;
}
ts-message.highlight {
    background: #404040 !important;
    border-radius: 20px !important;
}

}
