css:
@-moz-document domain("corazondemelon.es") {
body {
 cursor:url(http://i60.tinypic.com/vzexw1.png), crosshairs !important;}
.forum_post {
border: 1px solid #AFC3E2;
margin: 5px 0;
background: #D5DDE8;
clear: both;
}
a {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
color: #27486B;
}
.friend .player-name a {
color: #7CBFDF;
font-size: large;
font-weight: bold;
margin-bottom: 6px;
display: block;
width: 140px;
}
span {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
color: #154961;
}
#logged-episode {
left: 6px;
top: 39px;
width: 88px;
height: 18px;
line-height: 18px;
color: #0C2935;
text-shadow: 2px -2px 2px #fff;
font-weight: bold;
font-size: 12px;
text-transform: uppercase;
text-align: right;
}
div {
margin: 0;
padding: 0;
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
font-size: 12px;
line-height: 20px;
color: #000;
}
#logged-pseudo a {
color: #23607C;
text-decoration: none;
}
#logged-pseudo span {
float: left;
color: #0C2935;
text-shadow: 2px -2px 2px #fff;
font-weight: bold;
font-size: 14px;
}
h3, .integrated-title3 {
font-family: Nilland;
color: #1E3E66;
font-size: 11pt;
margin: 12px 0 6px;
text-transform: uppercase;
}
#right-informations #counter h2 .firstword .number {
letter-spacing: 5px;
font-family: Nilland;
font-size: 15pt;
color: #123F5E;
padding-right: 0;
background-position: -4px 2px;
}
.boyfriend h1 {
display: inline-block;
}
h1 {
color: #1D5C85;
font-size: large;
margin: 0 0 16px;
padding: 0 8px;
text-transform: uppercase;
}
h1 {
border: 0;
outline: 0;
vertical-align: baseline;
background: transparent;
font-family: sans-serif;
line-height: 20px;
}
.boyfriend .show-comments, .boyfriend .show-comments-no-comment {
color: #0A255E;
clear: both;
display: block;
}
.boyfriend .lom span {
background-color: #144877;
text-transform: uppercase;
color: white;
font-weight: bold;
font-size: x-small;
padding: 2px;
}
#astrological-sign a {
text-decoration: none;
font-family: Nilland;
font-weight: bold;
font-size: 13px;
color: #0E1F64;
}
.title-forum a {
text-decoration: none;
display: block;
font-size: 115%;
color: #122435;
}
#punwrap thead {
border: 1px solid #081C27;
border-bottom: 0;
background:#1B4D7C;
}
h2 .firstword, .integrated-title2 .firstword {
font-family: Nilland;
font-size: 14pt;
color: #21558F;
font-style: normal;
}
#container.connected h2 {
font-family: Nilland;
margin: 12px 0 6px;
border-bottom: 1px dashed #cfcfcf;
}
h2, .integrated-title2 {
font-family: Nilland;
font-size: 14pt;
font-style: italic;
color: #102738;
text-transform: uppercase;
}

#profile div#avatar {
background: white url('http://i57.tinypic.com/akv51c.png') center bottom no-repeat;
position: relative;
width: 260px;
height: 400px;
overflow: hidden;
border: 2px #aed05d solid;
border-radius: 10px;
-moz-border-radius: 10px;
z-index: 1;
}
#news-list .news a {
text-decoration: none;
color: #102D63;
font-weight: bold;
}
#episodes_list .episode_wrap {
position: absolute;
width: 522px;
height: 406px;
background-image: url("http://i58.tinypic.com/30iizbp.png");
background-repeat: no-repeat;
}
#sous-menu {
width: 100%;
height: 40px;
position: relative;
margin-top: -20px;
background: url("http://i58.tinypic.com/2zthq8i.png") no-repeat scroll top center transparent;
}
#container{
background-image : url(http://i58.tinypic.com/s2ql8k.png) !important ; background-repeat: no-repeat;
}
#container.connected #header { background-image : url(http://i58.tinypic.com/4qh37q.png) !important ;
}
.idcard-admin {
background-image: url('http://i62.tinypic.com/27xqu5y.jpg');
}
.idcard-moderator {
background-image: url('http://i62.tinypic.com/330ahvo.jpg');
}
.idcard-member { background : url(http://i59.tinypic.com/2sbv8n9.jpg) no-repeat !important ;
}
}
@-moz-document regexp("http://www.corazondemelon.es/profil/daizy106") {
.idcard-member { background : url(http://i59.tinypic.com/2sbv8n9.jpg) no-repeat !important ;
}
}
