css:
/* PassThePopcorn - Friendly Edits
 * Last updated 7 May, 2014 */
@-moz-document domain(passthepopcorn.me) {

* a:active, * a:hover { outline:0 !important; }

#searchbar_log,
#nav_irc,
#userrecommended
{ display:none !important; }

.search-bar__search-field { font-size:12px !important; }
.search-bar__search-field__input { background:none !important; background-color:rgb(103,0,1) !important; width:140px !important; }

.user-info-bar {
   border-radius:0 !important;
   font-size:11px !important;
}
.user-info-bar a, .user-info-bar a:hover { text-decoration:none !important; }
.user-info-bar a:hover { color:#eee !important; }

.forum-post--unread { border:thin solid rgb(160, 0, 14) !important; }

}
