@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("greasyfork.org") {
  a {text-decoration:none !important;}
/* 整体 */
  html {background: /*[[bi]]*/ Lavender !important;}
  body {width:1010px !important;margin:0 auto !important;box-sizing:border-box;/*[[bo]]*/}
  body:not([class]) {background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAByCAYAAAB5lADlAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsIAAA7CARUoSoAAAAAiSURBVDhPY2RgYPgPxAxMIAIERhlAMMoAglEGEAxzBgMDAMczAeNv/jdzAAAAAElFTkSuQmCC");background-repeat:repeat-x;
  padding:10px 25px;}

/* 头部 */
  #main-header {margin-left:0px !important;height:96px !important;background:none !important;}
  /* 左侧 */#site-name-text h1 {line-height:80px !important;}
   #main-header .subtitle {font-size:11px !important;}
  /* 右侧 */#site-nav {margin:0px !important;}
    /* 上方 */#script-search input[name] {width: 130px !important;}
    /* 下方 */#site-nav nav li {display:inline-block !important;margin-left:0px !important;}
    #site-nav nav li:not(:nth-of-type(4)) {margin-right:-4px;}
    #site-nav nav a {text-decoration:none !important;
    padding: 0px 10px !important;}
  .sign-in-link,.user-profile-link {margin-right:10px !important;}
/* A sample of our scripts */
  section > ul > li {margin-bottom:10px !important;} 
/*meta*/
  :-moz-any(.script-list,#user-script-list) footer {margin-top: 10px !important;}
  :-moz-any(.script-list,#user-script-list) dl {max-width: 940px !important;-moz-column-count: inherit !important;}
  :-moz-any(.script-list,#user-script-list) dt {
  width:auto !important;padding-right: 10px !important;float: none !important;
  text-align: left !important;font-weight: 600 !important;display: inline-block !important;}
    dt::after {content: "："}
  :-moz-any(.script-list,#user-script-list) dd {float: none !important;display: inline-block !important;margin-right: 15px !important;}
  dt.script-list-author {width: 60px !important;}
  dd.script-list-author {width: 80px !important;}
  dd.script-show-version {width: 110px !important;}
  dd.script-list-author,dd.script-show-version {
  white-space:nowrap; overflow:hidden;text-overflow:ellipsis;
  vertical-align: middle;}
  dd.script-show-applies-to {width: 850px !important;}
/*page*/
  .pagination em {font-style:normal !important;}
  .pagination a,.pagination em,.pagination span {padding:3px 6px !important;}
  .pagination a {background:rgba(255,255,255,.5) !important;}
  .pagination a:hover {background:rgba(255,255,255,.7) !important;}
}

@-moz-document regexp("https://greasyfork\.org/scripts/?"),
url-prefix("https://greasyfork.org/scripts?page=") {

  #script-list-option-groups {margin:0px !important;
  float:none !important;width:100% !important;font-size:15px !important;}
   #script-list-option-groups div {padding:4px 8px !important;margin-bottom:0px !important;
   border-radius:0px !important;}
   #script-list-sort ul,#script-list-sort li,#script-list-filter ul,#script-list-filter li {display:inline-block !important;}
   #script-list-sort li,#script-list-filter li {margin-right:10px !important;}
  #script-list-filter ul {width:750px !important;vertical-align:top !important;}
  
  #browse-script-list a {text-decoration:none !important;color:DarkBlue !important;}
  #browse-script-list dl {max-width:100% !important;font-size:15px !important;margin:10px 0px 15px !important;}
  #browse-script-list li{border-bottom: 2px #333 dashed !important;margin-top: 10px !important;}

}

@-moz-document regexp("https://greasyfork.org/scripts/.*/?") {
  #script-links li {margin-bottom:0px !important;}
  #script-links li:not(first-child) {margin-right:-5px !important;}
  #script-links .current {position:static !important;padding:0px !important;}
  
  #script-links {border-bottom:1px solid black}
  #script-content {border:none !important;}
  #additional-info img {max-width: -moz-available !important;}
  #additional-info div {background:none !important;}
  #script-meta {padding-bottom:10px !important;}
  #additional-info:not(:empty) {border-top: 2px #333 dashed}

}

/* Code From @chbwdbd */
@-moz-document regexp("https://greasyfork.org/scripts/[^\/]+/code.*$") {
#script-content {border-top:none !important;margin-top:10px !important;padding-top:0px !important;
  overflow-x:auto !important;width:960px !important;overflow-y:auto !important;height:660px !important;
background:rgba(255,255,255,.5) !important;padding-left:0px !important;}
  #script-content pre {background:none !important;border-right:none !important;}
}
