css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.merbivore.com") {

/* ****************************** */
/* * GENERAL */
/* ****************************** */
body {
  color: #000 !important;
  background: #152C4C !important;
  font-family: "Lucida Grande","Lucida Sans",Helvetica,Verdana,sans-serif !important; }

h1 {
  border-bottom: 2px solid #26518A !important;
  color: #0D192B !important; }

h2 {
  color: #152C4C !important; }

pre {
  background-color: #0C182A !important;
  border: 1px solid #26518A !important;
  color: #E6E0DB !important; }

/* ****************************** */
/* * HEADER */
/* ****************************** */
#header {
  background: #0D192B !important;
  height: 105px !important;
  padding-top: 7px !important; }

#branding h1 {
  border-bottom: none !important;
  height: 85px !important;
  margin-top: 10px !important;
  text-indent: -9999px !important;
  background: url(http://datamapper.org/images/logo.gif) no-repeat top left !important; }

/* ****************************** */
/* * NAVIGATION */
/* ****************************** */
ul#navigation {
  margin-right: 0 !important;
  margin-top: 70px !important;
  width: 25em !important; }
  ul#navigation li a {
    border-top: 2px solid #0D192B !important;
    color: #B7BAC0 !important;
    font-size: 1.1em !important;
    font-weight: normal !important;
    margin-right: 2px !important;
    padding-bottom: 12px !important;
    text-decoration: none !important;
    text-transform: uppercase !important; }
    ul#navigation li a#active, ul#navigation li a:hover {
      background: transparent !important;
      background-color: #152B4A !important;
      border-top: 2px solid #26518A !important;
      color: white !important; }

/* ****************************** */
/* * CONTENT */
/* ****************************** */
#notice p {
  color: #FFF !important; }

#content {
  background-color: white !important; }
  #content a {
    color: #26518A !important;
    font-weight: bold !important; }
  #content a:hover {
    background: #26518A !important;
    color: white !important;
    text-decoration: none !important; }

/* ****************************** */
/* * SIDEBAR */
/* ****************************** */
#sidebar {
  color: white !important; }
  #sidebar a {
    color: #66FF33 !important; }
  #sidebar h2 {
    color: white !important;
    font-weight: normal !important;
    letter-spacing: 1px !important;
    text-transform: uppercase !important; }

/* ****************************** */
/* * FOOTER */
/* ****************************** */
#footer {
  background: #0B1829 !important; }
  #footer p {
    color: #4676C0 !important; }
  #footer a {
    color: white !important; }

#defensio-plug img {
  margin-bottom: -4px !important; }

/* ****************************** */
/* * ADMIN SECTION */
/* ****************************** */
#edits .edit .deletion {
  border-left-color: #B71118 !important; }
#edits .edit .addition {
  border-left-color: #11B73C !important; }

#content .ruby .com 		 { color: #BC9358; }
#content .ruby .string  { color: #A4C260; }
#content .ruby .keyword { color: #CB7832; }
#content .ruby .symbol  { color: #6C9CBD; }
#content .ruby .sclass  { color: #FFF; }
#content .ruby .bool    { color: #6C9CBD; }
#content .ruby .number  { color: #A4C260; }
#content .ruby .attr    { color: #6C9CBD; }
#content .ruby .regexp  { color: #6C9CBD; }

}
