css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.baidu.com") {

html{
    overflow-y:visible;
}    

body{
    width:100%;
    overflow-x:hidden;
}    

#lh,#cp,#lk,#mCon,map,#bottom_container,#ftConw{
    display:none;
}
    
.s-lite,.s-skin,.s-treasure,.s-set-page{
    display:none!important;
}

#nv a{
    text-decoration:none;
}
    
#m{
    display:block;
    position:relative;
    top:90px;
    width:100%;
}
    
#nv{
    position:relative;
    display:inline-block;
    text-indent:0;
    margin:auto;
    display:none;
}
    
#fm,#s_fm{
    padding:0;
    text-align:center;
}
    
#fm .s_ipt_wr{
    background:white!important;
    width:100px!important;
}
    
#form1{
    display:inline-block!important;
    margin:auto!important;
    position:relative;
    left:45px;
}
#fm #kw,#kw1{
    margin:auto;
    width:540px;
    height:45px;
    padding:0;
    margin:0;
    padding-right:10px;
    margin-right:80px;
    border-top-right-radius:3px;
    border-bottom-right-radius:3px;
    text-indent:10px;
}
    
.btn_wr,.s_btn_wr{
    box-shadow:none;
    background:#2932E1;
    height:47px;
    width:90px;
    position:relative;
    border:0;
    box-shadow:none;
    border-top-right-radius:2px;
    border-bottom-right-radius:2px;
    right:90px;
}

#form1 .s_btn_wr:after,#form1 .btn_wr:after{
    content:" ";
    width:27px;
    height:27px;
    display:inline-block;
    color:white;
    font-size:27px;
    line-height:27px;
    position:relative;
    bottom:36px;
    text-align:center;
    pointer-events: none;
    background: url('http://cn.bing.com/s/a/hpc12.png') no-repeat scroll 0% 0% transparent;
    background-position: -224px -53px;
}
.btn_wr #su,.btn_wr #su1,.s_btn_wr #su,.s_btn_wr #su1{
    height:47px;
    width:90px;
    border:0;
    box-shadow:none;
    color:white;
    opacity:0;
}
    
#form1 .s_ipt_wr{
    width:auto!important;
    border:0;
}
/*header*/
#head{
    position:absolute!important;
    top:0;
    height:120px;
    width:100%!important;
    clear:both!important;
    background:#F1F1F1;
}
/*header u*/
#u{
    position:absolute!important;
    top:20px;
    right:10px;
    height:30px;
    text-align:right;
    padding-right:10px;
}

#u span,#u a{
    color:#505050!important;
    padding: 0 3px;
    text-decoration:none;
    line-height:30px;
    font-size:12px;
}

#u #userMenu a{
    color:#333!important;
    font-size:11px;
}

#u #userMenu a:hover{
    background:#EEE;
    width:58px!important;
    overflow:hidden;
}
    
#u i{
    display:none;
}
/*header search*/
#head form{
    display:none;
    position:absolute!important;
    top:20px!important;
    left:125px;
}
    
#head .s_ipt_wr{
    background:white!important;
    width: 570px!important;
    border-bottom:rgb(217,217,217) 1px solid;
    border-left:rgb(217,217,217) 1px solid;
    border-top:rgb(192,192,192) 1px solid;
    border-right:rgb(192,192,192) 1px solid;
}
    
#head .s_ipt_wr input{
    width: 560px;
}
    
.bdsug{
    width: 565px!important;
    text-align:left!important;
}
    
#head .s_btn_wr{
    background:none;
    background-color:transparent;
}

#head #su{
    border-radius:2px;
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    background-color: #4D90FE;
    background-image: -moz-linear-gradient(center top , #4D90FE, #4787ED);
    border: 1px solid #3079ED;
    color:white;
}
    
/*header nav*/
#head .s_logo{
    display:none;
}
    
#head .s_nav{
    position: absolute!important;
    height:50px;
    left:0;
    bottom:0;
    padding-left:125px!important;
    background:white;
    width:100%;
    border-bottom:1px #EBEBEB solid;
}
    
#head .s_nav #s_tab{
    line-height:50px;
    padding:0;
}
    
#head .s_nav #s_tab a, #head .s_nav #s_tab b{
    display:inline-block;
    text-decoration:none;
    padding:0 8px;
    height:47px;
}
    
#head .s_nav #s_tab a{
    color:#777;
    transition:color .15s linear;
}
    
#head .s_nav #s_tab a:hover{
    color:black;
}
    
#head .s_nav #s_tab b{
    text-decoration:none;
    padding:0 5px;
    color:#DD4B39;
    border-bottom:#DD4B39 3px solid;
}
    
#wrapper_wrapper{
}

/*container*/
#container{
    position:relative!important;
    top:175px!important;
    margin-left:100px;
    margin-bottom:180px;
}
    
.hit_top .c-icon-bear{
    display:none;
}
    
#content_left,#content_right{
    margin-top:20px;
}
#content_left .c-container a,#content_left .result-op a,#content_left .c-container em,#content_left .result-op em{
    text-decoration:none;
}
#content_left .c-container .t{
}
    
#content_left .c-container .t a{
    font-size:18px!important;
    text-decoration:none;
    color:#1E0FBE;
}
        
#content_left .c-container .t .op_url_guanwang{
    font-size:13px!important;
}
#content_left .c-container .t em{
    font-size:18px!important;
    text-decoration:none;
}
    
    
#content_left .c-container em{
    color:#DD4B39;
}
    
#content_left .c-container em{
    color:#DD4B39;
}
    
.result, .c-containter{
    margin-bottom:20px!important;
}
    
.c-abstract{
    color:#545454;
    line-height:1.4!important;
}
    
/*.c-gap-top-small{
    position:relative;
    left:-138px;
    transition: all .4s .2s ease-out;
}
    
.c-gap-top-small:hover{
    left:0;
}*/
    
.extquery a{
    color:#1E0FBE;
}

.c-border{
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.2)!important;
    border:none;
    margin:8px 0;
}
    
.c-gap-bottom-small{
    color:#DD4B39;
}

.c-gap-icon-left{
    color:white!important;
}
    
.m{

}
    
.g{
    color:#008000;
}
    
.c-span-last{
    color:#545454;
}
    
#content_right{
    opacity:0;
    border-left-color:#EEE!important;
    transition:opacity .15s .2s linear;
}
        
#content_right a{
    text-decoration:none;
}
    
#content_right:hover{
    opacity:1;
    border-left-color:#EEE!important;
}
#foot{
    display:none;
}

/*container search*/
#search,#rs{
    margin-left:75px!important;
}
#rs{
    position:absolute!important;
    top:100px;
    left:40px;
    border-bottom:#EFEFEF 1px solid;
}
    
#rs a{
    text-decoration:none;
    color:#1E0FBE;
    font-size:13px;
}
    
#rs .tt{
    vertical-align:middle;
    font-size:18px;
    font-weight:normal;
    width:100px;
}
    
#search form{
    position:absolute!important;
    top:20px;
    padding-left:20px;
}
    
#search .s_ipt_wr,#kw,#kw1{
    background:white!important;
    border-bottom:rgb(217,217,217) 1px solid;
    border-left:rgb(217,217,217) 1px solid;
    border-top:rgb(192,192,192) 1px solid;
    border-right:rgb(192,192,192) 1px solid;
}
    
#search .s_ipt_wr{
    width:600px;
}
    
#search .s_btn_wr{
    background:none;
    background-color:transparent;
}

#search .s_btn{
    position:relative;
    right:6px;
    border-top-right-radius:2px;
    border-bottom-right-radius:2px;
    
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    background-color: #4D90FE;
    background: -moz-linear-gradient(center top , #4D90FE, #4787ED);
    border: 1px solid #3079ED;
    border-left: 0;
    color:white;
    font-family:"Microsoft Yahei";
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
}
    

#search .s_btn:hover{
    background: -moz-linear-gradient(center top , #4D90FE, #357AE8);
}
    
#search .s_help{
    position:absolute;
    top:0;
    width:250px;
    height:30px;
}
    
#search .s_help a{
    width:55px!important;
    display:inline-block;
    color:#CCC;
    font-size:13px;
    text-decoration:none;
    float:left;
    margin-left:18px;
    line-height:30px;
    transition:all .218s linear;
}
#search .s_help a:hover{
    color:#666;
}
    
    
#search .s_help a:nth-child(2),#search .s_help a:nth-child(3),#search .s_help a:nth-child(4){
    display:none;
}
}

@-moz-document regexp("https?://www.baidu.com/.*tn=baidufir")
{
    
tbody{
    display:none;
}
    
div{
    position:absolute;
    top:220px;
    height:110px;
    width:100%!important;
    clear:both!important;
    background:#F1F1F1;
    font-size:15px!important;
    line-height:55px!important;
    margin-top:200px;
    padding-left:70px;
}
    
form{
    position:relative;
    display:block!important;
    width:600px;
    float:right;
    bottom:135px;
    margin-right:80px;
}
    
input[type="text"]{
    background:white!important;
    border-bottom:rgb(217,217,217) 1px solid;
    border-left:rgb(217,217,217) 1px solid;
    border-top:rgb(192,192,192) 1px solid;
    border-right:rgb(192,192,192) 1px solid;
    height:40px;
    font-size:20px;
    line-height:40px;
    font-family:"Microsoft Yahei";
    width:400px;
}

input[type="submit"]{
    border-radius:2px;
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    background-color: #4D90FE;
    background: -moz-linear-gradient(center top , #4D90FE, #4787ED);
    border: 1px solid #3079ED;
    color:white;
    font-family:"Microsoft Yahei";
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    height:40px;
    width: 100px;
    margin-left:20px;
}
    

input[type="submit"]:hover{
    background: -moz-linear-gradient(center top , #4D90FE, #357AE8);
}

#ft{
    display:none;
}
    

}

@-moz-document regexp("http://www.baidu.com/gaoji/advanced.*") {

#tl{
    position:absolute!important;
    top:0;
    right:0;
    height:30px;
    text-align:right;
    padding-right:10px;
    width:100%;
    background:#2D2D2D;
    border-bottom:black 1px solid;
}
    
#lg{
    height:100px;
}
    
#lg a,#ft{
    display:none;
}

#tl span,#tl a{
    
    color:#CCC!important;
    text-decoration:none;
    line-height:30px;
    font-size:12px;
    text-decoration:none;
}
    
#tl > b:nth-child(1){
    display:none;
}
    
#tl > b:nth-child(2){
    position:absolute!important;
    font-size:20px;
    font-weight:normal;
    top:60px;
    left:55px;
    color: #DD4B39;
}

#t1{
    border-top:#EBEBEB 1px solid;
    border-bottom:#E5E5E5 1px solid;
    background:#F5F5F5;
}

td{
    font-size:15px;
    height:30px;
    padding:5px 0;
}
input{
    
    height:30px;
}
input,select{
    font-size:15px;
    line-height:30px;
}

#t1{
    padding-top:50px!important;
    padding-bottom:30px!important;
    margin-bottom:20px;
}
    
#t1 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > b:nth-child(1){
    position:absolute;
    top:135px;
    left:50px;
    font-weight:normal;
    font-size:18px;
    font-style:oblique;
}
    
#t1 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > b:nth-child(1):before{
    position:absolute;
    font-family:arial;
    content:"“";
    color:#CCC;
    font-size:48px;
    left:60px;
    top:45px;
    font-style:normal;
}
    
#t1 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > b:nth-child(1):after{
    content:" …";
    
    
}
    
input[type="radio"]{
    height:10px!important;
}
#t1 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2),
#t1 > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1),
#t1 > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1),
#t1 > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1){
    padding-left:30px;
    color:#333;
    width:200px;
}

input[type="submit"]{
    position:absolute;
    top:280px;
    right:260px;
    border-radius:2px;
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    background-color: #4D90FE;
    background: -moz-linear-gradient(center top , #4D90FE, #4787ED);
    border: 1px solid #3079ED;
    color:white;
    font-family:"Microsoft Yahei";
    transition: all 0.218s ease 0s, visibility 0s ease 0s;
    height:40px;
    width: 100px;
    margin-left:20px;
}
    

input[type="submit"]:hover{
    background: -moz-linear-gradient(center top , #4D90FE, #357AE8);
}
    
#keyword,
input[name="q2"],
input[name="q3"],
input[name="q4"]{
    width:500px!important;
}

body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2),
body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2),
body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(2),
body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(2),
body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(5) > td:nth-child(2),
body > form:nth-child(4) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(6) > td:nth-child(2){
    padding-left:30px;
}
}
