css:
@-moz-document url-prefix("http://tieba.baidu.com/f?"), url-prefix("http://tieba.baidu.com/p/") {
/*侧边浮动工具栏*/
.tbui_aside_float_bar,
/*贴条*/
.tb_paper_wrap,
#j_worldcup_info_banner,
/*头部(标题、导航)*/
.card_head,
.forum_header .card_banner,
.game_frs_in_head,
.cont_sign_bottom,
.j_tbnav_tab:nth-child(5),
.j_tbnav_tab:nth-child(6),
.j_tbnav_tab:nth-child(7),
.j_tbnav_tab:last-child,
/*侧栏*/
#aside,
.right_section,
.tb_rich_poster .poster_head_text a.add_guessing_btn,
/*左侧浮动*/
#j_ten_years,
/*浮动广告*/
.BAIDU_CLB_AD,
/*右上角用户信息*/
#com_userbar .split,
#com_userbar .u_app,
#com_userbar .u_tshow,
#com_userbar .u_tbmall,
#com_userbar .u_wallet{
  display:none;
  width:0;
  height:0;
  line-height:0;
  overflow:hidden;
  margin:0;
  padding:0;
}
#com_userbar{
  left: 50%;
  margin-left: 342px;
}
/*搜索区域*/
.tbsug_expand .tbsug_tip .tbsug_title , 
.search_bright .search_inp_border, 
.search_nav a:link, 
.search_nav a:hover, 
.search_nav a:visited{
  font-family:"XHei",Arial;
}
/*头部简化*/
.card_top {
  padding-left:20px; 
  border-bottom:1px dotted #cdcdcd;
}
.card_top_theme2 .card_top, .card_top_theme1 .card_top{padding-left:20px;}
.forum_header{
  position:relative;
  border-bottom:1px dotted #cdcdcd;
}
.card_top_right{margin:-50px 0 0 0;}
#tb_nav{
  position:absolute;
  top:5px;
  right:5px;
  background:none;
  height:auto;
}
#tb_nav .nav_list{
  width:380px;
  background-color:#EDEEF2;
  float:none;
}
.nav_wrap, .nav_list .space, 
.nav_list .focus, 
.nav_list .focus .tbnav_tab_inner{
  background:none;
}
.nav_list li:hover, 
.nav_list li:hover .tbnav_tab_inner{
  background:#E5E7EA;
}
.search_internal_wrap {
  margin:10px 180px 10px 0;
}
.top_cont_toggle{
  z-index:10001;
}
/*帖子列表*/
.main{
  width:978px;
}
.threadlist_bright .threadlist_li_right{
  width:860px;
}
.threadlist_bright .tb_icon_author .sign_highlight, 
.threadlist_bright .tb_icon_author .sign_highlight:hover,
.threadlist_bright .tb_icon_author .sign_highlight:visited{
  color:#999;
}
.thread_topic_bright .tt_guidetexture{
  width:855px;
}
.tb-editor-editarea-wrapper{
  width:857px;
}
.thread_topic_bright .topic_thread_wrapper,
#tt_text_cnt{
  display:none;
}
.j_thread_list,
.thread_list_bottom{
  background-color:#fff;
}
/*内容页*/
#j_core_title_wrap, 
.l_post_bright{
  width:978px;
}
.d_post_content_main{
  width:827px;
}
.l_post_bright .core_reply_wrapper{
  width:auto;
}
.threadlist_bright .thread_pic_bright{
  width:auto;
}
.threadlist_bright .threadlist_img{
  width:878px;
}
.threadlist_bright .imgtopic_album{
  width:755px;
}
/*侧栏*/
.ui_card_wrap.doupiao_offline{
  display:none;
}
}

@-moz-document url-prefix("http://tieba.baidu.com/p/") {
.content{
  position:relative;
}
#tb_nav{
  position:absolute;
  top:16px;
  right:0;
  width:390px;
  height:46px;
  overflow:hidden;
}
.nav_wrap_add_border{
  border:none;
}
.ul_often_forum{
  display:none;
}
.left_section{
  width:978px;
  overflow:hidden;
}
.core_title_wrap_bright{
  overflow:visible;
  width:738px;
  padding-right:240px;
  
}
.core_title_wrap_bright.core_title_absolute_bright{
  padding-right:0;
}
#j_core_title_wrap .core_title{
  width:978px;
}
.core_title_btns{
  z-index:10010;
}
.lzl_simple_wrapper{
  padding-right:250px;
}
.lzl_editor_container .lzl_panel_wrapper{
  width:780px;
}
}
