css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guildwars2.com") {
    html, div#content, dt, td, body, th, table {
        font-family: "Linux Biolinum O", Geramond, "Times Rioman";
        color: #aaaaaa !important;
        background: #222222 !important;
        font-size: 16px;
    }
    
    div.infobox-schauplatz {
        border-color: #857B60;
    }
    
    table.abgerundet, table.wikit, .abgerundet, th {
        border-radius: 4px;
        border: 0px solid #333333 !important;
    }
    
    #p-cactions li {
        border: none;
    }
    
    div{
        border-color: #222222 !important;
        background: transparent !important;
    }
    
    div#globalWrapper, input, select {
       font-family: "Linux Biolinum O", Geramond, "Times Rioman";
    }
    
    h5, h2, h3, h4, h1 {   
        border-bottom: 1px solid #383838;
    }
    
    h1 {
        font-size: 140%;
        color: #DEBD67;
    }
    
    h2 {
        font-size: 120%;
        color: #CA9510;
    }
    
    h3, h4, h5 {
        font-size: 100%;
        color: #A88427 !important;
    }
    
    dt {
        color: #dddddd !important;
    }
    
    span, code
    {
        background: transparent !important;
    }
    
    div#content {
        z-index: 0;
    }
    
    #toc, .toc {
        border: none;    
    }

    #p-cactions li a {
        background-color: #333333;
        height: 18px;
        font-size: 100%;
        margin: 5px;
        border-radius: 2px;
    }
    
    #p-cactions li a:hover {
        background-color: #444444;
    }
    
    #p-cactions li.selected a {
        z-index: 3;
        background-color: #333333;
        font-size: 100%;
        color: black;
        padding: 5px;
        border: 1px solid #555555;
        border-radius: 3px;
    }

    #p-cactions li.selected a:hover {
        background-color: #444444;
    }

    #p-cactions li.selected {
        border: 1px solid #222222;
    }
    a {
        color: #A88427 !important;
    }

    a:visited {
        color: #A329CC !important;
    }

    li {
        background : #222222 !important;
    }

    div.thumb.tright, div.thumb.tleft {
        border: none !important;
        background: #222222 !important;
    }
}
