css:
@-moz-document domain("noteflight.com") {
div#appHeaderBand
{
background-color: black !important;
}
div#content
{
background-color: black;
}
div.headerMenuItem
{
color: white !important;
font-weight: bold !important;
}
div.headerSubMenu, div.activityEvent
{
background-color: #292929 !important;
}
a.headerMenuItem
{
color: white !important;
}
a.headerMenuItem:hover
{
color: black !important;
}
.actionButton
{
background-color: whitesmoke !important;
border: none !important;
color: black !important;
}
div.headerSubMenu.infoMenu, div.info
{
background-color: #292929 !important;
}
a.infoNavItem
{
color: whitesmoke;
}
a.infoNavItem:hover
{
color: white !important;
}
div.headerSearchBar
{
background-color: #292929 !important;
}
div.headerSubMenu.standardSubMenu.searchBar
{
background-color: #292929 !important;
color: white;
}
div.collectionItem.clearFloat.current
{
background-color: #1A1A1A !important;
}
div.collectionItem.clearFloat:hover, div.post.clearFloat, div.accountForm
{
background-color: #292929 !important;
}
div.infoStrip
{
background-color: grey !important;
}
div.scoreSocialPanel.scoreSocialPanel.showing, div.comment.clearFloat
{
background-color: #292929 !important;
}
button.primary
{
background-color: white !important;
color: black !important;
}
a.standardLink
{
color: grey !important;
}
div.scoresViewType
{
background-color: grey;
}
div.scoresViewType:hover
{
background-color: #CCCCCC !important;
}
div.scoresViewType.list.selected
{
background-color: grey !important;
}
li.current
{
background-color: #1A1A1A !important;
}
li:hover
{
background-color: #292929 !important;
}
div.scoreRow.clearFloat
{
background-color: #292929
}
div.scoreRow.clearFloat.editable
{
background-color: #292929 !important;
}
div.title
{
color: white !important;
font-weight: bold;
}
div.announcementBanner
{
background-color: black;
border-bottom: 0px;
}
a.noLink
{
color: white;
font-weight: bold
}
div.announcementClose
{
background-color: grey;
}
div.auxInfo
{
color: white !important;  
}
div.separator
{
border-color: #292929 !important;
}
div.columnLabel
{
color: white !important;
}
body
{
color: white;
}
div.post, div.formLabel, div.pod.breathe
{
color: white;
}
}
