css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filehippo.com") {

#entire,#header,#content{
  width:1100px !important;
  font-family:arial rounded MT bold !important;
}

#entire,a{
  font-family:arial rounded MT bold  !important;
  font-size: 1.2em !important;
  line-height:18px !important;
}

#entire{
  font-family:arial rounded MT bold  !important;
  font-size: 1.2em !important;
  line-height:18px !important;
  -moz-border-radius-bottomright: 1em !important;
  -moz-border-radius-bottomleft: 1em !important;
}

p,li,.desc,small,h1,em{
  font-family:arial rounded MT bold  !important;
}

.box-three{
  width:350px !important;
  padding-left: 12.5px !important;
}

#content-full{
  width:1100px !important;
  padding-bottom: 30px !important;
  -moz-border-radius: 20px; border-color: #000 !important;
}

h2{
  -moz-border-radius: 20px; color: #000 !important;
  height:14px !important;
}

.box-two{
  width: 500px !important;
  padding-left: 25px !important;
}

.ad,.bottombar,.copybar{
  display:none !important;
}

.pathbar,#upperbar{
  width: 1080px !important;
  font-family:arial rounded MT bold  !important;
}

#feeds{
  padding-bottom:50px !important;
}

.right{
  padding-right:70px !important;
}

.right table,.middle{
  width:1080px !important;
}

.middle{
  width:800px !important;
}

#dlbox{
  -moz-border-radius: 20px; border-color: #fff !important;
  background: #c0c0c0 !important;  width:200px!important;           
  overflow:   scroll !important;
  max-height: 40em  !important;
}


}

@-moz-document url(http://www.filehippo.com/),
               url-prefix(http://www.filehippo.com/latest/),
               url-prefix(http://www.filehippo.com/popular/),
               url(http://www.filehippo.com/software/multimedia/),
               url(http://www.filehippo.com/software/antispyware/),
               url(http://www.filehippo.com/software/p2p/),
               url(http://www.filehippo.com/software/browsers/),
               url(http://www.filehippo.com/software/firewalls/),
               url(http://www.filehippo.com/software/dvd/),
               url(http://www.filehippo.com/software/messaging/),
               url(http://www.filehippo.com/software/antivirus/),
               url(http://www.filehippo.com/software/desktop/),
               url(http://www.filehippo.com/software/cleaning/),
               url(http://www.filehippo.com/software/ftp/),
               url(http://www.filehippo.com/software/compression/),
               url(http://www.filehippo.com/software/graphics/),
               url(http://www.filehippo.com/software/office/),
               url(http://www.filehippo.com/software/benchmark/),
               url(http://www.filehippo.com/software/drivers/),
               url(http://www.filehippo.com/software/developer/),
               url(http://www.filehippo.com/software/networking/)
{

.right{
  width:1000px !important;
}


}
