@namespace url(http://www.w3.org/1999/xhtml);
/**
 * @title KrainaUkraina
 * @version 0.1
 * @description
 *  Стиль для сайту проекту "МОЯ КРАЇНА – УКРАЇНА"
 *  (народний казково-пізнавальний серіал).
 *  http://krainaukraina.com/
 *  Виправляємо непомітні посилання до перегляду на Ютубі.
 */
@-moz-document domain("krainaukraina.com") {
    .moduletable_s11 A {
        font-family: Arial, sans-serif !important;
        font-size: 18px !important;
        line-height: 1em !important;
        display: block;
        width: 2em !important;
        height: 2em;
        padding: 0.5em 1em !important;
        text-indent: 5em;
        overflow: hidden;
        margin: 1em auto 0 auto !important;
        text-decoration: none !important;
        background-color: #ee2020 !important;
        color: #fff !important;
        text-align: center !important;
        border-radius: 0.8em;
        position: relative;
        transition: background-color 0.5s;
    }

    .moduletable_s11 A:before {
        display: block;
        content: "►";
        position: absolute;
        left: 50%;
        top: 50%;
        font-size: 2em;
        line-height: 1em;
        text-indent: 0;
        margin-top: -0.5em !important;
        margin-left: -0.4em !important;
    }

    .moduletable_s11 A:hover {
        background-color: #ff2020 !important;
        transition: background-color 0.5s;
    }
}
