@namespace url(http://www.w3.org/1999/xhtml);
@charset "utf-8";

/* i really want this to be global */
/* CSS Document */

/* Gmail Readability
================================================================== */

.a3s {
font-family: 'Meta Serif', "DejaVu Serif", "Bitstream Vera Serif", "Liberation Serif", Georgia,  Constantia, "Lucida Bright", Lucidabright, "Lucida Serif", Lucida, serif;
font-size: 22px;
line-height: 1.636;
max-width: 650px;
margin-left: auto;
margin-right: auto;
padding-top: 3.5em;
padding-bottom: 1.5em;
color: #333;
}
    
.a3s a,
.a3s a:link {
color: #505050;
text-decoration: none;
border-bottom: 1px dotted #999;
}
    
.a3s a:hover {
color: #08C;
border-bottom: 1px solid #999;
background: #fafafa;
}
