css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://twitter.com/") {

    .dashboard-right, .trends-container, .module trends, .flex-module, .DashboardProfileCard-bg, .bird-topbar-etched, .prompt-wrapper, .promptbird, .tweet-translation, .translate-button {
       display: none !important;
    }
    
    .u-cf {
        float: right;
    }
    
    .expand-stream-item {
        margin-right: 94px !important;
    }
    
    .collapse-stream-item {
        margin-right: 64px !important;
    }

    .dashboard-left {
        position: fixed;       
        top: 40px;
        width: 100%;
        left: 0;
        z-index: 200 !important;
    }
    
    .content-main {        
        position: absolute;
        margin-left: 12.6% !important;
        top: 180px;
        width: 70% !important;
        box-shadow: 2px -2px 6px rgba(0,0,0,0.2),
                     -2px 0 6px rgba(0,0,0,0.2);
    }
    
    .DashboardProfileCard-tweetbox {
        width: 98.2% !important;     
        box-shadow: 1px 1px 4px rgba(0,0,0,0.4);
        border-radius: 0 !important;
    }   
    
    #tweet-box-mini-home-profile {
        height: 22px !important;
    }
    
    .stream-item {
        z-index: -1 !important;
    }                
    
    .DashboardProfileCard-stats {
        padding: 0;

    }
    
    .DashboardProfileCard-statList, .Grid {
        margin: 1px auto 5px 36px !important;
        display: inline-block !important;
    }
    
    .DashboardProfileCard-avatarImage {
        display: none;
        position: fixed;
        top: 250px;
        left: 56px;
        width: 100px;
        height: 100px;
    }
    
    .Arrange-sizeFit {
        width: 160px !important;
    }
    
    .DashboardProfileCard-userFields {
        top: 18px;
        margin-left: 81%;
        font-size: 10px !important;
        color: #66757F;
        z-index: 200 !important;
    }
    
     .DashboardProfileCard-screennameLink {
       padding-left: 20px !important;
       display: none !important;
          color: #444;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu;
    }
    
    global-nav-search {
       position: absolute;
       top: 46px;
       right: 0;
       height: 20px !important; 
    }
    
  .search-input {
        border-radius: 2px !important;
        height: 26px !important; 
        margin-top: 4px;
    }
    
    .Grid {
        padding-right: 240px;
        margin-right: 44%;
        margin-top: 1px !important;
        margin-bottom: 6px !important;
        text-align: left;
    }
    
   .tweet-btn {
        padding: 4px 6px;
        margin-top: 2px;
  
   }
    .clear-recent-searches {
        color: #66757F !important;
    }
    
    .fullname {
        font-size: 14px !important;
    }
    
    .tweet-text {
        font-size: 14px !important;
    }
    
    .tweet-actions-sidebar {
        text-align: right !important;
        margin-right: 12px !important;
    }
    
    .tweet-actions-sidebar > li {
        width: 30px !important;
        margin-top: 10px !important;
    }
    
    .tweet-action-count {
        margin-right: -10px !important;
    } 
    
    .fullname {
        color: #666 !important;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu;
    }
    
    .tweet-text, #global-actions {
        color: #444;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu !important;
    } 
    
    .DashboardProfileCard-name {
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu !important;
        color: #849098;
    }
    
    .stream-item-header, .stream-item-footer, .DashboardProfileCard-statList {
        color: #444 !important;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu;
    }
    
    .with-icn, .search-input {
        color: #777 !important;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu;
        opacity: 0.5;
    }
    
   .new-tweets-bar {
        color: #777 !important;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu;
        opacity: 0.8;
    }
    
    .dropdown-link, .embed-link {
        color: #777 !important;
        text-shadow: 0 0 0 #222;
        font-family: Ubuntu !important;
        opacity: 0.9;
    }
    
    .js-global-new-tweet {
        color: #eee !important;
        text-shadow: 0 0 0 #fff;
        font-family: Ubuntu !important;
        font-weight: bold !important;
    }
    
    .tweet-box, .header-inner, .content-header {
        color: #999 !important;
        text-shadow: 0 0 0 #fff;
        font-family: Ubuntu !important;
    }
   .expand-stream-item, .collapse-stream-item {
       position: absolute !important;
       right: 150px !important;
       margin-top: 12px;
   }
}
