css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

    .ambox-Refimprove, #siteNotice {
        display: none !important;
    }

    .Template-Fact {
        color: #FEE;
        background: #FEE;
    }
    .Template-Fact a * {
        display: none;
    }
    .Template-Fact a:before {
        content: "!";
        color: #D66;
        font-style: normal !important;
    }
}
