@-moz-document url-prefix(http://www.mininova.org/com/) {

body p a {
  font-family:bauhaus !important;
}

p {
  font-size:1.5em !important;
  color: #fff !important;
  font-family:bauhaus !important;
}

#thanks {
  font-size:1.0em !important;
}

#action-download {
  font-size:1.3em !important;
}

.comment h3 a {
   margin-left: 10px !important;
}

.comment h3 {
  padding-right: 200px !important;
}

.comment.dark p,.comment.light p {
  font-size:1.3em !important;
  color: #999 !important;
  margin-left: 65px !important;
}

.comment.dark p a,.comment.light p a {
  color: #4cff00 !important;
  border-color: #000 !important;
}

.comment.dark {
  background-color: transparent !important;
  border: 0px solid #555 !important;
  font-family:bauhaus !important;
}

.comment a:visited {
  opacity: .5 !important;
  text-decoration: none !important;
}

.comment.dark a:hover {color: #9000d3 !important;}

.comment.light a:hover {color: #f80!important;}

.comment.special a:hover {color: #00ffcb!important;}

.comment.uploader a:hover {color: #9000d3!important;}

.comment.light, #thanks {
  background-color: transparent  !important;
  border: 0px solid #444 !important;    
  font-family:bauhaus !important;
  color: #fff  !important;
}

.comment.special h3 { 
border-color: #fff !important;
}

.comment.special {
  background-color: #000  !important;
  border: 0px solid #333 !important;
  font-family:bauhaus !important;
  font-size:1.05em !important;
}

.comment.special p{
  margin-left: 65px !important;
  font-size:1.3em !important;
  color: #ff0000 !important;
  opacity: 0.5 !important;
}

.comment.special small{
  color: #ff0000 !important;
  opacity: 0.5 !important;
  font-size:1.35em !important;
}

.comment h3, .comment h3 a {
  border-color: #fff !important;
  font-family:bauhaus !important;
  color: #fff  !important;
}

.comment h3 a {
  color: #fff  !important;
  border-color: #000 !important;
}

.comment.uploader {
  background-color: #000 !important;
  border: 0px solid #FF8800 !important;
  font-family:bauhaus !important;
  color: #999 !important;   
}

.comment.uploader small{
  color: #ff8800 !important;
  border-color: #000 !important;
  opacity: 1.0 !important;
  font-size:1.35em !important;
}

.comment.uploader h3 {
  border-color: #FFf !important;
  text-align:left !important;
}

.comment.uploader p a {
  color: #4cff00 !important;
}

.comment.uploader p  {
  margin-left: 60px !important;
  font-size:1.35em !important; 
}

