css:
@-moz-document domain("habrahabr.ru") {
/* Светлый фон с тостера */
body {
  background: #EEE;
}

/* Белый фон на странице «Доступ к публикации закрыт» */
body.register {
  background: #FFF;
}



/* Увеличиваем максимальную ширину */
#layout {
  max-width: 1620px;
}



/* «Лишний» пиксель у активного пункта меню */
.content_left table.menu tr td {
  border-bottom-color: #EAECEA;
}



/* Старый логотип */
#navbar .nav_panel .logo {
  background-image: url('http://beta.hstor.org/files/467/182/a26/467182a26514415ea803d1f85c0d654b.png');
  background-position: 0 0;
  -moz-background-size: 0 0;
  -webkit-background-size: 0 0;
  -o-background-size: 0 0;
  background-size: auto;
}



/* «Тихие» счётчики в панели */
#navbar .nav_panel .tab .count {
  color: #888;
  background: transparent;
}

#navbar .nav_panel .tab .count:before {
  content: '+';
}



/* Прижимаем панель к левому краю */
#navbar {
  left: 0;
}



/* Больше отступы для контентной части (не затрагивая страницы с кастомными стилями) */
body:not([style]) #layout .inner {
	padding-top: 48px;
	margin-left: 36px;
	margin-right: 36px;
}

@media only screen and (max-width: 1740px) {
  body:not([style]) #layout .inner {
    margin-left: 96px;
  }
}
}
