css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://), url-prefix(file://) {
  ::-moz-selection {
    background-color: #4c9ed9 !important;
    color: #f5f7fa !important;
  }
  #header::-moz-selection {
    background-color: #4c9ed9 !important;
    color: #f5f7fa !important;
  }
  #top link + #header {
    background-color: #EBECED !important;
    border-bottom: 2px solid #E4E4E5 !important;
    outline: 1px solid #AAAAAA !important;
  }
  #top link + #header p {
    margin: 0 !important;
    padding: 0.4em 0.6em !important;
  }
  #top .start-tag, #top .end-tag {
    color: #0A3860 !important;
  }
  #top .attribute-name {
    color: #2C699F !important;
    font-weight: normal !important;
  }
  #top .attribute-value {
    color: #B7520E !important;
  }
  #top .comment {
    color: #757772 !important;
  }
  #top .cdata {
    color: #2C699F !important;
  }
  #top .doctype {
    color: black !important;
    font-style: italic !important;
  }
  #top .pi {
    color: #2C699F !important;
  }
  #top .entity {
    color: #B7520E !important;
  }
  #top .markupdeclaration {
    color: #0A3860 !important;
  }
}
